package com.example.horgszmobilalkalmazs;

import android.content.ContentValues;
import android.content.Context;
import android.database.Cursor;
import android.database.sqlite.SQLiteDatabase;
import android.database.sqlite.SQLiteOpenHelper;
import android.util.Log;
import android.view.View;

import androidx.annotation.Nullable;

import com.google.firebase.firestore.CollectionReference;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;

import java.util.ArrayList;

public class DatabaseHal extends SQLiteOpenHelper {
    String LOG_TAG = DatabaseHal.class.getName();
    private static final String TABLE_NAME = "halak_table";
    private static final String COL0 = "nev";
    private static final String COL1 = "latinNev";
    private static final String COL2 = "imageResourceId";
    private static final String COL3 = "tipus";
    private static final String COL4 = "tilalmiIdoszak";
    private static final String COL5 = "legkisebbKifoghatoMeret";
    private static final String COL6 = "legkisebbKifoghatoMeretTilalmiIdoszakban";
    private static final String COL7 = "leiras";
    private final FirebaseFirestore mFirestore = FirebaseFirestore.getInstance();
    CollectionReference mItems = mFirestore.collection("Halak");

    public DatabaseHal(@Nullable Context context) {
        super(context, TABLE_NAME, null, 1);
    }

    @Override
    public void onCreate(SQLiteDatabase db) {
        String createTable = "CREATE TABLE " + TABLE_NAME + " (" + COL0 + " TEXT PRIMARY KEY, " + COL1 + " TEXT , " + COL2 + " NUMBER, " + COL3 + " TEXT, " + COL4 + " TEXT, " + COL5 + " NUMBER," + COL6 + " NUMBER," + COL7 + " TEXT" + " )";
        db.execSQL(createTable);
        Log.i(LOG_TAG, "Oncreate database lefutott");
        //fillLocalStore();
    }

    @Override
    public void onUpgrade(SQLiteDatabase sqLiteDatabase, int i, int i1) {

    }

    public long addHalToLocalStore(ClassHal hal){
        SQLiteDatabase db = this.getWritableDatabase();
        ContentValues contentValues = new ContentValues(8);
        contentValues.put(COL0, hal.getNev());
        contentValues.put(COL1, hal.getLatinNev());
        contentValues.put(COL2, hal.getImageResourceId());
        contentValues.put(COL3, hal.getTipus());
        contentValues.put(COL4, hal.getTilalmiIdoszak());
        contentValues.put(COL5, hal.getLegkisebbKifoghatoMeret());
        contentValues.put(COL6, hal.getLegkisebbKifoghatoMeretTilalmiIdoszakban());
        contentValues.put(COL7, hal.getLeiras());

        return db.insert(TABLE_NAME, null, contentValues);
    }

    public void fillLocalStore(){
        if (getSize() == 0){
            for (ClassHal hal: halak) {
                addHalToLocalStore(hal);
            }
        }

    }

    public void fillFireStore(){
        for (ClassHal hal: halak) {
            mItems.add(hal);
        }
    }

    public ArrayList<ClassHal> getAllDataFromLocalStore(){
        SQLiteDatabase db = this.getWritableDatabase();
        String query = "SELECT * FROM " + TABLE_NAME;
        Cursor data =  db.rawQuery(query, null);
        ArrayList<ClassHal> halakArray = new ArrayList<>();
        if (data != null && data.moveToFirst()){
            do {
                halakArray.add(new ClassHal(data.getString(0), data.getString(1), data.getInt(2), data.getString(3), data.getString(4), data.getInt(5), data.getInt(6), data.getString(7)));
            } while (data.moveToNext());
        }
        return halakArray;
    }

    public void getAllDataFromFireStore(){
        ArrayList<ClassHal> halakTomb = new ArrayList<>();
        mItems.orderBy(COL0).get().addOnSuccessListener(queryDocumentSnapshots -> {
            for (QueryDocumentSnapshot documentSnapshot : queryDocumentSnapshots) {
                ClassHal hal = documentSnapshot.toObject(ClassHal.class);
                halakTomb.add(hal);
            }
            if(halakTomb.size() != 0){
                deleteHalak();
                for(ClassHal h : halakTomb){
                   addHalToLocalStore(h);
                }
            } else {
                fillLocalStore();
            }
        });
    }

    public ClassHal getHalByNev(String nev){
        ArrayList<ClassHal> halak = getAllDataFromLocalStore();
        for (ClassHal hal: halak) {
            if (hal.getNev().equals(nev)){
                return hal;
            }
        }
        return null;
    }

    public int getSize(){
        SQLiteDatabase db = this.getWritableDatabase();
        String query = "SELECT * FROM " + TABLE_NAME;
        return db.rawQuery(query, null).getCount();
    }

    public void deleteHalak(){
        SQLiteDatabase db = this.getWritableDatabase();
        db.execSQL("DELETE FROM " + TABLE_NAME);
        db.close();
    }

    final ClassHal[] halak = {
            new ClassHal("Amur", "Ctenopharyngodon idella", R.drawable.amur, "Idegenhonos faj", null, 0, 0,"Eredetileg csak Kelet-Ázsiában, az Amurban és az attól délre eső nagy folyókban élt, de sok helyre betelepítették. Eredeti hazájában tipikus folyóvízi hal, nálunk főként állóvizekben, csatornákban telepedett meg, de nagy folyóink nem túl gyors áramlású szakaszian is előfordul. Hazai állománya telepített példányokból alakult ki. Nagy növésű hal, méteres hosszúságot és akár 30 kg feletti tömeget is elérhet. Teste megnyúlt, oldalról csak enyhén összenyomott, majdnem hengeres. Hát- és hasvonala kevéssé ívelt, közel párhuzamos. Feje kicsi, homloka széles, orra hát-hasi irányban lapított, a két orrnyílás között húzódó barázda miatt egy kissé homorú. Szája félig alsó állású, ajkai vaskosak. Hátúszója, kicsivel előrébb kezdődik, mint a hasúszója. Pikkelyei nagyok, erősen ülnek. A fiatal amur kezdetben planktonnal, majd apró gerinctelen állatokkal táplálkozik, később áttér a növények leveleinek és puhább hajtásainak fogyasztására. A növényi részeket erős és vaskos ajkaival tépdesi le. Intenzív táplálkozása a meleg nyári időszakra jellemző, ilyenkor saját testtömegének megfelelő mennyiségű táplálékot vesz magához naponta. Emiatt erősen környezet-átalakító faj, a hínárnövényeket, a nádat és a gyékényt képes jelentős mértékben eltüntetni, és mivel emésztésének hatásfoka gyenge, a vízbe visszajutó ürülék az algák számára könnyen hozzáférhető növényi tápanyagot biztosít, melynek kedvezőtlen hatása, hogy az eredetileg áttetsző vizű hínáros-nádas tavakat algás vizekké alakítja. Ívása természetes vizeinkben is előfordulhat, de sikeres szaporodását eddig nem tapasztalták. Eredeti élőhelyén a folyók 20 fokosnál melegebb áramló vizében csoportosan ívik, ikrái a vízben lebegnek."),
            new ClassHal("Amurgéb", "Perccottus glenii", R.drawable.amurgeb, "Idegenhonos, invazív faj", null, 0,0, "Az amurgéb – ahogyan a neve is utal rá – eredeti élőhelye az Amur folyó vidéke, valahol Oroszország, Kína és Korea közös határán. Hazánkban először 1997-ben a Tisza-tó területén észlelték. Az amurgéb a környezeti tényezőkkel szemben nagyon igénytelen, elviseli az oxigénhiányt, és azt is, ha télen mindössze néhány centiméternyi víz marad a vastag jégréteg alatt.Ez az alvógébfélék családjába tartozó faj, ami azt jelenti, hogy a hasúszói nem nőttek össze tapadókoronggá, ez különbözteti meg az igazi gébektől. a folyók főmedrében ritka, de a hullámtéri mélyedésekben, kubikgödrökben és az elöregedett holtágakban sűrű népessége alakulhat ki. Kis termetű hal, a nagyobb példányok is csak 15–20 centiméteresek lehetnek. Színre és alakra hasonlít hozzá a lápi póc, de annak csak egy hátúszója van., míg az amurgébnek két hátúszója van. Teste kissé zömök, mérsékelten ívelt hát- és hasvonalú, oldalról enyhén lapított. Nagy fejéhez mérten a szemei kicsinek tűnnek. Vastag ajkakkal keretezett fölső állású szájának szeglete a szem alá ér. A szájhasíték ferdén fölfelé irányul, az állkapcsokon apró fogak sorakoznak. A hasúszók kivételével úszói jól fejlettek, a szélük lekerekített. K Apró pikkelyei nemcsak a testet, hanem a fej hátsó részét is beborítják. Alapszíne a környezettől függően sárgás- vagy sötétbarna, amelyet nagyobb sötét foltok és kisebb világos pettyek mintáznak. Fejét a szemek körül keskeny sötét sávok díszítik. Szaporodáskor a hímek sötét nászruhát öltenek, homlokuk duzzadttá válik. Bár a nagyobbak halakat is fogyasztanak, táplálékának zömét vízi gerinctelen állatok, elsősorban rovarlárvák teszik ki. Május-júniusban szaporodik, az ikrát víz alatti tárgyakra, faágakra, gyökerekre ragasztják, amit megtermékenyítés után a hím őriz."),
            new ClassHal("Angolna", "Anguilla anguilla", R.drawable.angolna, "Őshonos, fogható faj", null, 0, 0,"Méretét tekintve kivételes esetben egyméteresnél nagyobbra is nőhet, de a kifejlettek egyedek átlagosan a 60–80 centiméteresek. Jellemzően a tejesek a kisebbek, és még ezek közül is egyes példányok egészen kicsik maradnak, hosszú évek alatt is csak 25-30 cm-es méretet érnek el. Táplálkozását tekintve ragadozóhal, fenéklakó mivolta miatt elsősorban a víz mélyén előforduló kisebb állatokkal táplálkozik. Feje enyhén lapított, a száj felé elkeskenyedően kúpos, és mint minden halnál, az angolnánál is nyak nélkül kapcsolódik az állat törzséhez. A fejtől egyenletesen kiszélesedő, hengeres, kígyószerű törzs átmenet nélkül kapcsolódik a farokrészhez. Különálló páratlan úszói nincsenek, a hát-, a farok- és a farokalatti úszó egységes úszószegéllyé olvadt össze. Az egyetlen páros számú úszó a mellúszó, amely a legutolsó kopoltyúfedő lemez tövénél kezdődik. Alig észrevehető pikkelyei igen aprók, bőre erősen nyálkás, síkos. Színe eleinte zöldesszürke, később barnás, végül az ivarérett, vándorlásra kész példányok ezüstös színűvé válnak. Az angolna kizárólag az Atlanti-óceán nyugati részén található Sargasso-tengerben szaporodik, a lárvák az áramlatok segítségével két év alatt érkeznek meg Európa partjaihoz, és egy újabb év múlva kezdik meg útjukat a folyókon fölfelé. A hazai angolnaállomány szinte teljes egészében az európai folyótorkolatokból származó angolnák betelepítésével jött létre. Igen hosszú életű halfaj, akár 100 évig is élhet."),
            new ClassHal("Bagolykeszeg", "Ballerus sapa", R.drawable.bagolykeszeg, "Őshonos, fogható faj", null, 0, 0,"A folyók áramláskedvelő keszegfaja, ennek megfelelően inkább csak az erősebb sodrású szakaszokon fordul elő. A tavak és víztározók közül azokban él, amelyekben áramlások is vannak. Nyúlánk, de idősebb korban magas hátú, oldalról lapított testű hal. A hátúszó kezdete jóval hátrébb esik, mint a hasúszó elhelyezkedése. Feje kicsi, orra rövid és nagy ívben lekerekített, a szeme nagy „bagolyszem”. Kicsi, félig alsó állású szája van. Hosszú farokalatti úszója miatt hasonlít hozzá a laposkeszeg, de annak az orra hegyes, a szája fölső állású. Alakra és szájállásra nézve hasonlít hozzá a dévérkeszeg, de annak farokalatti úszója rövidebb. Jól fejlett példányai is csak maximum 30 cm hosszt érnek el. Mélyen bemetszett farokúszóján az alsó lebeny kissé hosszabb a felsőnél. Pikkelyei közepes nagyságúak, úszói sárgásszürkék. Elsősorban fenéklakó gerinctelen állatokat, valamint szerves törmeléket és növényi eredetű táplálékot fogyaszt. Szaporodása májustól júniusig tart a folyók sodrottabb szakaszain, illetve a tavak áramlásos részein. A lerakott ikra a homokos-kavicsos aljzathoz tapad."),
            new ClassHal("Balin", "Aspius aspius", R.drawable.balin, "Őshonos, fogható faj", "03.01.-04.30.", 40, 0,"A balin valamennyi nagyobb folyó-, illetve állóvizünkben megtalálható, a pontyfélék családjába tartozik, foga nincs, mégis ragadozóhal. Testhossza a 70-80 cm-t is elérheti. Félig felső állású szája nagy, szájszöglete a szem alá nyúlik. Alsó állkapcsának csúcsa kissé felfelé hajló, enyhén kampós. Teste megnyúlt, mérsékelten magas, oldalról lapított. Színe, hátoldalon grafitszürke, oldalán ezüstösen csillogó, hasa fehér. Pikkelyei aprók, számuk az oldalvonalon 65-75. Úszói erőteljesek, nagyok, az alsó úszók a fiataloknál rózsaszínesek, az idősebbeknél vörhenyesek. Szeme nagy, szemgyűrűje citromsárga. A hátúszója nagy első sugara hosszú és kemény. Eleinte állati planktonnal, majd főleg vízre pottyanó rovarokkal táplálkozik, később halivadékok szerepelnek étlapján, csak kifejletten tér át a ragadozó életmódra. Gyakran kerül szem elé, mert nem lesből támad, hanem áldozatát kitartóan űzi, melynek szemtanúi is lehetünk, ahogyan a sekély vízben hajtja a fő táplálékát adó küszrajokat. Márciusban kezdi az ívást, az ikrákat főleg kavicsos aljzatra rakja."),
            new ClassHal("Bodorka", "Rutilus rutilus", R.drawable.bodorka, "Őshonos, fogható faj", null, 0, 0,"Hazánk minden vizében megtalálható. Rendkívül jól alkalmazkodó kistermetű halfaj, kifejlett példányai legtöbbször 10-18 cm-esek. Oldalról lapított testű, nem túl magas hátú hal, melynek szája csúcsba nyíló, középállású. A has- és hátúszók első sugarai a hal hossztengelyére merőlegesen egy vonalba esnek, míg a nagyon hasonló vörösszárnyú keszeg a hátúszójának első sugara nem esik egybe a hasúszó alapvonalával. A hasvonal, a has- és a farokalatti úszók között hengeres, él nincs rajta. Szemgyűrűje halvány narancssárgától az élénk narancsvörösig változik, a háta zöldesszürke, a hasa fehéres, a páros úszók enyhén vörhenyesek, a páratlanok szürkésvörösek. Oldalvonalán 40-46 pikkely számlálható. Rovarlárvákat, apró csigákat, planktonrákokat, hínárhajtásokat fogyaszt. Április elején, 10-12 °C-os vízhőmérsékleten csoportosan ívik."),
            new ClassHal("Botos kölönte", "Cottus gobio", R.drawable.botos_kolonte, "Védett faj", null, 0,0,"Korábban Európa szerte jelen volt, de jelentősen csökkent az állománya és szűkült az előfordulási területe, így ma már Magyarországon is ritkaságszámba megy. Nincs gazdasági jelentősége, az élőhelyeinek elvesztése pedig azt jelentette, hogy veszélyeztetté vált a faj és emiatt mára már védettséget is élvez. Európai elterjedtségű faj az Atlanti-óceán partjaitól az Urál hegységig megtalálható, a homokos kavicsos talajú tiszta oxigéndús folyóvizekben. A Kárpát-medence peremrészein és magasabb hegységeinek vizeiben gyakori. Magyarországon őshonos, de csak elvétve található meg a neki megfelelő élőhely hiánya miatt. Előfordulhat a Duna egyes szakaszain az Ipoly, a Dráva és a Tisza vizében. Legjobban a Magyarországon még ritkábban előforduló cifra kölönte hasonlít hozzá, de annak oldalvonala a test hátsó részén a hátúszó alatt megszakad és minden úszóját sötétebb harántsávok díszítik. A magyarországi vizekben is megtalálható gébfélék is hasonlítanak rá, de ezektől eltérő, hogy azoknak a testét pikkely borítja és fejük is keskenyebb. A hal teste bunkót formázóan megnyúlt, elöl hengeres, hátulsó részén oldalról összenyomott. Feje nagyon széles, fölülről békaszerűen lapított. Kopoltyúnyílása szűk szája nagy csúcsba nyíló, szöglete a szem elejének vonalához közelít, állkapcsain apró fogacskák ülnek. Szeme magasan helyezkedik el, aránylag nagy, de átmérője kisebb az orra hosszánál. Teste csupasz, nincsenek pikkelyei, oldalvonala teljesen végighúzódik a testén, egészen a farok úszóig. Sárgásszürke alapszínét sötétbarna foltok tarkítják. Két hátúszója közül az első 5-9 tüskés sugárral, a második 13-19 lágy sugárral, farok alatti úszói 10-15 lágy sugárral rendelkeznek, mellúszói nagyok alsó sugaraik túlnyúlnak az úszó bőrrészén. Méretét tekintve apró termetű hal testhossza 10-12 centiméter. A testében egyáltalán nincs úszóhólyag ezért a vízben tartósan lebegni nem tud. A botos kölönte tiszta, sebes sodrású, köves és kavicsos medrű folyóvizekben a fenéken tartózkodik. Gyakori elterjedési területe a pisztrángos hegyi vizek, de a magyarországi csekély vízhozamú középhegységi és dombvidéki patakokban nem tud megélni. A nagyobb folyók pér- és paduczónájában is otthon érzi magát, sőt előfordul a márnazóna fölső régiójában is, de csak abban az esetben, ha a víz hőmérséklete nem emelkedik túlságosan magasra, és a tisztasága is megfelelő. Napközben rejtőzködő életmódot folytat, táplálkozni alkonyatkor indul. Falánk ragadozó hal, fő tápláléka a pataki bolharák (Gammarus pulex), különböző rovarlárvák és felnőtt korban a lakóhelyén található halak, akár saját fajtársai is."),
            new ClassHal("Cifra kölönte", "Cottus poecilopus", R.drawable.cifra_kolonte, "Védett faj", null, 0, 0,"Kis méretű gazdasági jelentőséggel nem bíró faj, mely nagy kiterjedésű területen Észak- és Közép- Európától Szibérián át egészen az Amur vízrendszeréig megtalálható. Magyarországon nagyon ritka és védett gyakorlatilag csak a Tisza vízrendszerében, az esetleg lesodródott példányaival lehet találkozni. A cifra kölönte elterjedési területe Skandinávia, Északnyugat-Oroszország, Szibéria és Közép-Európa északi része. A Kárpát-medence keleti és északi részein számos hegyi vízfolyásban megtalálható így a Fekete-Tisza, a Fehér-Tisza, a Visó, az Iza, a Szaplonca, a Sajó, a Hernád és az Ipoly, folyókban, azonban Magyarország jelenlegi területén lévő folyóvizekben előfordulása nagyon ritka. Közeli rokonához a botos kölöntéhez, hasonlít a legjobban, de annak oldalvonala megszakítás nélkül tart a farokúszó tövéig, míg a cifra kölöntének ez megszakad az oldalvonala mentén. A két kölöntefaj egyébként együttesen ritkán fordul elő azonos folyószakaszon így ez is segíthet a megkülönböztetésükben. Hasonló lehet hozzá még a különféle gébek népes tábora, de a cifra kölönte teste csupasz ezért a gébeket fedő jellegzetes pikkelyeik és a különböző formájú hasúszóik alapján viszonylag könnyen meg lehet különböztetni őket. Hengeres a teste mely két oldalról lapított farokban végződik. Testhossza általában 7-9, centiméter, legfeljebb 12,5 centiméter. Nagy feje feltűnően széles és lapos. Széles csúcsba nyíló száját húsos ajkak keretezik és az állkapcsait apró fogak borítják. Szájszöglete nem éri el a szem vonalát és a testén nincsenek pikkelyek. Kopoltyúfedőin nagyon erős, hajlott tüske van. Az oldalvonal a test középvonala felett húzódik, és csak körülbelül a második hátúszó közepe alá ér; 20-25 apró csontpikkely borítja. A hasúszók vége eléri a végbélnyílást és gyakran azon is túlér. Színe sötét tónusú foltokkal tarkított olívzöld vagy barnásszürke és a hasa sárgásfehér. Az első hátúszó szegélye narancssárgás, mely különösen ívás idején lesz jól látható. A hasúszót keskeny harántsávok díszítik. A farokúszó tövénél gyakran sötét folt látható. A cifra kölönte homokos vagy kavicsos talajú, tiszta, oxigéndús vizeket kedveli, oxigénigénye 8 mg/l. Igazi élőhelye a hegyi patakok és folyók pisztrángzónájában van. A felmelegedést nem bírja, ezért a 24 Celsius-foknál melegebb vizekben elpusztul. A kövek között és a görgeteg alatt rejtőzködő fenékhal. Tápláléka fenéklakók, főként bolharákok, halikra és ivadék."),
            new ClassHal("Compó", "Tinca tinca", R.drawable.compo, "Őshonos, fogható faj", "05.02.-06.15.", 25, 0," A gazdag vízi növényzettel rendelkező sekély állóvizek tipikus faja, folyók mentén a holtmedrekben, az áradások után kialakuló mellékvizekben fordul elő. Kifejlett példányainak hossza 20-30 cm, de 40 cm feletti méretet is elérhet. A pontyfélék családjába tartozik, testalkata zömök, oldalról enyhén lapított. Olyan külső bélyegekkel rendelkezik, amely alapján egyértelműen elkülöníthető más halfajoktól. Feje aránylag nagy, szeme kicsi. Úszói lekerekítettek. Apró pikkelyekkel fedett testét rendkívül vastag nyálkaréteg borítja. Színében a zöldes árnyalatok dominálnak, de fekete, sárga és piros színváltozata is előfordul. Szája csúcsba nyíló, szájszegletében egy-egy bajusz található. Főként iszapból kitúrt apró állatokkal táplálkozik, de étrendjét növényi részek és magvak is színesítik. Szaporodása májustól júniusig elhúzódhat, ikráit vízinövényekre rakja."),
            new ClassHal("Csuka", "Esox lucius", R.drawable.csuka, "Őshonos, fogható faj", "02.01.-03.31.", 40, 0,"Magyarország gyakori, jól alkalmazkodó ragadozóhala, amely a hegyvidéki patakok kivételével szinte minden víztípusban megél. Nagyobb állományai a dús növényzetű, de nyílt vízzel is rendelkező sekély állóvizekben jellemzőek. Hosszú életű, nagyra növő hal, kivételesen 1 méternél és 20 kg-nál is nagyobb lehet. Teste hosszúkás és erőteljes, a hát- és a hasvonal szinte párhuzamos. Feje hosszú, orra a kacsa csőrére emlékeztet. Szája nagyra nyitható, benne nagy, tűhegyes fogak helyezkednek el, amelyek a horog kivételekor könnyen megsérthetik a horgász kezét, de még a szájpadláson és a kopoltyúíveken is vannak fogai. Hátúszója a teste végén található, szinte a farokalatti úszóval egyvonalban kezdődik. Nagy szájának kemény alsó állkapcsa túlér a felsőn, formája lapított és mélyen vágott. Fölső állkapcsának hátsó széle a viszonylag nagy szem alá ér. Alsó állkapcsán hegyes, erős és kúpos fogak helyezkednek el, melyek elölről hátrafelé haladva egyre nagyobbak. Pikkelyei aprók, színük a halvány ezüst-aranyostól az olajzöld alapon arany foltos, halványan csíkozott változatokig terjedhet. Az úszókat sötétebb, szabálytalan foltok mintázzák. Tápláléka zömében hal, de szinte mindent megeszik, ami a vízben él és mozog. Nem ritkán zsákmányol saját testméreténél alig kisebb halat. Legkorábban ívó halunk, ívása már a februárban elkezdődik, és márciusban végződik, ikráit a vizek növényzetére rakja."),
            new ClassHal("Csupasztorkú géb", "Babka gymnotrachelus", R.drawable.csupasztorku_geb, "Idegenhonos faj", null, 0, 0,"A csupasztorkú géb a Fekete-tenger nyugati és északi részébe, beleértve az Azovi-tengert is, ömlő folyókban és brakvizekben él. A Duna vízgyűjtő körzete Bukarest magasságáig. A Kaszpi-tengerben is megtalálható. A hal testhossza 12-15 centiméter, legfeljebb 16,2 centiméter. 6 centiméteresen már felnőttnek számít. 54-69, többnyire 56-68 pikkelye van egy hosszanti sorban. Fejtetője, tarkója, a mellúszók tövi része, kopoltyúfedői és torka pikkelyek nélküli. Nincs úszóhólyagja. Ez a mérsékelt övi gébféle egyaránt megél az édes- és brakkvízben is. Megtalálható a lagúnákban, tavakban, nagy folyókban, de a gyors folyású patakokban is. A dús vízinövényzettel borított, homokos és iszapos fenéken érzi jól magát. A 4–20 Celsius-fokos hőmérsékletet kedveli. Tápláléka mindenféle apró fenéklakó, például: felemáslábú rákok, árvaszunyoglárvák, soksertéjűek, valamint puhatestűek és kisebb halak is. Legfeljebb 5 évig él. Körülbelül 2 évesen válik ivaréretté. Április–júniusban ívik, néha augusztus közepéig is eltart az ívási időszaka. A nőstény, csak egy ívási időszakot él át, de ebben többször is ívhat. A hím őrzi és gondozza a ragadós ikrákat. Az ikrákat vízinövényekre, kövekre és puhatestűek házaira rakja le a nőstény."),
            new ClassHal("Domolykó", "Squalius cephalus", R.drawable.domolyko, "Őshonos, fogható faj", "04.15.-05.31.", 25, 0,"A domolykó igazi folyóvízi hal, melyről a jellegzetes hengeres teste is árulkodik. Testhossza elérheti az 50 cm-t is, tömege a 6-8 kg-ot is nyomhat. Vizeinkben a 3 kg-nál nagyobb példányok ritkának számítanak. Feje viszonylag nagy, a homlokrész széles, az orr tompán lekerekített. Csúcsba nyíló szája nagy, a szájszöglet eléri a szem elejének vonalát. A farokalatti úszó szegélye domború vagy egyenes, de sohasem homorú, ami a nyúldomolykóhoz hasonlítva a legfeltűnőbb megkülönböztető bélyeg. Háta feketés vagy barnászöld. A hasúszók és a farokalatti úszó az idősebb példányokon vörösek, a többi úszó sötétszürke. A fiatal példányoknál minden úszó szürkés színű. Táplálékát algacsoportok, növényi részek, apró rákok, férgek, csigák, vízre hulló rovarok és szerves anyagok, alkalmanként halivadék és ikra teszi ki. Május-júniusban ívik, ikráit a folyóvizek kavicsos mederszakaszaira rakja le."),
            new ClassHal("Dunai galóca", "Hucho hucho", R.drawable.duna_galoca, "Védett hal", null, 0, 0, "A dunai galóca korábban csak a Duna felső és középső szakaszának vízgyűjtő területén, különösen magában a Dunában és a jobb oldali mellékfolyóiban élt, napjainkban a Rajnába és a Rhône vizébe is betelepítették. Magyarországon a Duna és a Tisza felső szakaszán, valamint a Dráva-Mura vízrendszerében fordul elő, de nagy állományai itt sincsenek. Fokozottan védett halfaj, természetvédelmi értéke 100 000 Ft. Kifejlett egyedeinek testhossza 60-120 centiméter, mely ritka esetekben 150-200 centiméterhez is közelíthet. Testtömege a 30 kilogrammot is elérheti, sőt 50 kilogrammos óriás egyedeiről is van említés. Nyúlánk, áramvonalas testű, a feje jelentősen hosszabb a testének magasságánál. Feje nagy, lapított, szájnyílása széles. Az ekecsont fogazottsága: a lemez hátulsó szegélyén 4-8 kampós fog ül egyszerű keresztsávban, a nyél fogatlan. A hát- és farokúszó között nagy zsírúszó van. Testét közel egyforma nagyságú pikkelyek borítják, melyek nagyon kicsinyek, 180-200 darab van az oldalvonal mentén, és további 23-27 a zsírúszó és az oldalvonal között (beleértve az oldalvonal pikkelyeit is). Az első kopoltyúíven 16 kopoltyútüske van. Háta barnás vagy zöldesszürke, oldalai világosabbak, vörhenyes „rézfénnyel” a hasa fehéres. A fején feketés, kerek pettyek és kisebb pontok láthatók, amelyek egy része félhold alakú. A jól táplált példányok húsa piros, a rosszul tápláltaké ellenben fehéres színű. A dunai galóca oxigéndús, hideg, gyors folyású vizek lakója. Fiatal korában zooplanktonokkal táplálkozik, majd később lárvákkal és gerincesekkel és kisebb halakat is fogyaszt. A kifejlett példányai falánk ragadozóvá vállnak és lesből a balinhoz hasonló módon rárabol a kisebb halakra. Nagy oxigénigényének megfelelően a folyók felsőbb szakaszain, a kövezések mögött képződő visszaforgókban, a kanyarulatokat követő öblökben, a szigetek, és a kavicspadok mögött egyesülő vízfolyásokban tartózkodik. Területvédőfaj, azaz egy bizonyos területet véd és ott éli le életét. Március-áprilisban a nőstény az erős sodrású helyeken rakja le testkilogrammonként körülbelül 1000 ikráját. A kelési idő körülbelül 35 nap. Az ívás idején a hímek élénkebb színt vesznek fel, mint a nőstények."),
            new ClassHal("Dunai nagyhering", "Alosa pontica", R.drawable.dunai_nagyhering, "Védett faj", null, 0, 0, "A dunai hering elterjedési területe a Fekete- és az Azovi-tenger, valamint ezek folyói és a Paleostom-tó. A magyarországi Duna-szakaszból néhány példánya ismert, de mára kihalt. A Donban és a Dunában 567 kilométerre is felúszott; mielőtt megépítették a Kanyivi-víztározót, a Dnyeperben elérte Kijevet is. A Kaszpi-tengerben 2 alfaja, míg a Fekete-tengerben, az Azovi-tengerben és a beléjük ömlő folyókban 1 alfaja él: Alosa pontica kessleri - Kaszpi-tenger – 52 centiméter és 2 kilogramm is lehet;  Alosa pontica volgensis - Kaszpi-tenger Alosa pontica pontica - Fekete-tenger, Azovi-tenger és a beléjük ömlő folyók. A dunai hering teste karcsú, heringszerű, nagy, kerekded pikkelyekkel (30-36, átlagosan 33-34 van egy sorban). Testhossza 15-20 centiméter, legfeljebb 39 centiméterig. A Don folyóban nem nő 21 centiméternél nagyobbra. Hátúszója rövid. Oldalvonala nincs. Felső állkapcsa erőteljes középső bemetszéssel; alsó állkapcsa a szem hátulsó szegélyéig ér. Állkapcsa, nyelve, az ekecsont és a szájpadcsont fogazottak. Mell- és hasúszói rövidek, kicsinyek. 40-73 (átlagosan 46-55) erős kopoltyútüske, ezek rövidebbek, mint a kopoltyúlemezkék. Hátoldala és a fej felső fele sötét, kékesen vagy feketén csillogó. A szem és a kopoltyúfedő széle között sötét folt van. Nagyon ritkán 7-8 jelentéktelen sötét folt látható a kopoltyúfedők mögött. Ez a halfaj egyaránt megél a sós-, édes- és brakkvízben is. A tengerben 3-90 méteres mélységben tartózkodik. A kis rákok közül a Crangon-, Upogebia- és Idothea-fajokkal táplálkozik, míg a kishalak közül az Engraulis-, Clupeonella-, Sprattus-fajokat kedveli. Legfeljebb 7 évig él. A dunai hering március–áprilisban indul ívni, és gyakran nagyon messze felúszik a folyókban. Május végétől júliusig ívik, a Don felső szakaszán augusztusig. Ikráit homok- és kavicspadokra rakja. Az ivadékok június és szeptember között indulnak a tenger felé. A dunai hering 2-3 évesen ivarérett. A 20. század elején, a túlhalászás miatt, az összes dunai hering állomány erősen lecsökkent. Manapság is halásszák ezt a halfajt, de már nem ipari mértékben."),
            new ClassHal("Dévérkeszeg", "Abramis brama", R.drawable.deverkeszeg, "Őshonos, fogható faj", null, 0, 0,"Gyakori és legnagyobbra megnövő keszegfajunk, akár az 5 kg-os tömeget is elérheti. A nagyobb folyók lassú, síkvidéki szakaszának jellemző hala, azonban a tavak egy részében is komoly állománnyal van jelen. A nagyobb vizeket kedveli, a kisebb álló- és folyóvizekből hiányzik. Oldalról lapított, magas testű hal, a hát vonala gyengébben, a hasé erősen ívelt. A hátvonal ívében a nyakszirtnél egy jellegzetes törés figyelhető meg. A feje kicsi, orra legömbölyített, szeme közepes nagyságú. A száj félig alsó állású. Hátúszója magas, rövid, a farokalatti úszó hosszú. A hosszú farokúszó mélyen kivágott, alsó lebenye valamivel hosszabb a felsőnél. A fiatal egyedek úszói áttetszőek, színtelenek, a kifejlett példányoknál palaszürkék. Az oldalvonal és a hátúszó közötti hosszanti pikkelysorok száma 11-13, míg a karikakeszeg oldalvonala fölött csak 8–10 pikkelysor húzódik . A pikkelyek mérete az oldalvonaltól a hát íve felé haladva jelentősen csökken, a fölső sorokban már csak feleakkorák, mint az oldalvonal közelében. Eleinte algákkal, majd zooplankton szervezetekkel táplálkoznak. A kifejlett egyedek szúnyoglárvákat, kagylókat, csigákat és csővájóférgeket fogyasztanak. Ívása több szakaszban április végétől júniusig tart, az ikrákat növényzetre, ennek híján kövekre, kavicsos aljzatra rakja."),
            new ClassHal("Ezüstkárász", "Carassius gibelio", R.drawable.ezustkarasz, "Idegenhonos, invazív faj", null, 0, 0,"Eredeti hazája Délkelet-Ázsia, de ma már Európa túlnyomó részén megtalálható. Hazánkban az 1954. évi importját követően terjedt el, és ma már legtöbb vizünkben előfordul. Jól alkalmazkodik, a hegyi patakok kivételével szinte minden víztípusban megtalálható. Napjainkban vitatott kérdés, hogy idegen eredetű-e. A szakemberek szerint elképzelhető, hogy már a XIX. század előtt is jelen volt vizeinkben, csak kis számban. Tömeges elszaporodását elősegítethette tudatos telepítése a vizeinkbe, de ezenkívül talán más tényezőkkel is összefüggésbe hozható, amelyek időben egybeesnek a betelepítésekkel. Igazi élőhelyét a dús növényzetű sekély állóvizek jelentik, ahol nagy állományai alakulhatnak ki. Oldalról lapított, magas hátú, de zömök, vaskos testű hal. Barna vizekben a háta sötét, oldala sárgás. Tiszta, áttetsző vizekben világosabb, oldalai ezüstösen csillogóak, hasa fehéres. Átlagos mérete 20-30 cm, a feje viszonylag kicsi, orra tompa, szája kicsi és csúcsba nyíló, bajusza nincs. Legjobban a széles kárász hasonlít hozzá, de annak a hátúszója lekerekített, és a fiatalok faroknyelén fekete gyűrű vagy sötét folt látható. Hasonlít hozzá a ponty is, de annak fölső állkapcsán és a szájszögletében 1-1 pár pici bajusz található. Pikkelyeinek színe általában ezüstös, de a tőzeges vizekben élő példányoké aranysárga is lehet. Vegyes táplálkozású hal, ezért is képes a különböző vizekben megélni. Főként apróbb gerinctelen állatokat fogyaszt, de élőbevonatot, növényi anyagokat és szerves törmeléket is fogyaszt. Az ikrákat több részletben, május-júniusban rakják le a sekélyebb vizek növényzetére. Régebben kizárólag ikrás egyedekből álló állománya élt nálunk, amely más pontyfélék tejes egyedeivel ívott össze. Az utódok azonban kizárólag az anyák örökítőanyagát hordozták, ezért nem hibridek, hanem mind ikrás ezüstkárászok lettek. Három évtizede azonban megjelentek köztük a hímek is, és most már más fajokhoz hasonlóan is szaporodik. Ikráit a sekélyebb vizek növényzetére szórja."),
            new ClassHal("Fehér busa", "", R.drawable.feher_busa, "Idegenhonos, invazív faj", null, 0, 0,"Eredeti élőhelye Kína, a Kárpát-medencében nem őshonos. A 60-as évektől végzett rendszeres telepítések miatt a magyarországi folyókban és néhol tavakban előfordul. A teste oldalról erősen lapított, közepes testmagasságú. A szájszögletből húzott vízszintes vonal áthalad a szemen. A külső megjelenése rendkívül jellegzetes, nagy testű, feje testéhez képest nagy, de a pettyes busáénál (Hypophthalmichthys nobilis) kisebb, szemei az állcsúcs vonala alatt helyezkednek el. Szája felső állású, úszói jól fejlettek, farokúszója mélyen bemetszett. Háta szürke, esetleg zöldes árnyalatú. Oldalai és hasi része ezüstös csillogású. Átlagos testhossza 50-70 centiméter között van, azonban 105 centiméteresre és 50 kilogrammosra is megnőhet. Fito- és zooplanktonnal táplálkozik. A nyári hónapokban ívik (23-24 Celsius-fok), ikrái (nőstényenként 500 000-ig) szabadon sodródnak a vízben. Az ivadék eleinte állati planktonnal táplálkozik. Körülbelül 5-10 centiméteresek, amikor áttérnek a növényi planktonra, ehhez a bélcsatornájuk a testhossz 6-7-szeresére növekszik."),
            new ClassHal("Fekete törpeharcsa", "Ameiurus melas", R.drawable.fekete_torpeharcsa, "Idegenhonos, invazív faj", null, 0, 0,"Eredeti élőhelye Észak-Amerika középső és keleti része. Európába a törpeharcsával együtt került be. Előfordulására néhány nyugat-európai országban már felfigyeltek. Pontos európai elterjedési területét nehéz behatárolni, részben a folyamatos telepítések miatt, részben azért mert hosszú ideig sok helyen törpeharcsaként azonosították. Magyarországra 1980-ban érkezett meg Olaszországból a dinnyési tógazdaságba és 1981-ben Szarvason növekedési kísérleteket végeztek vele. Természetesen ez a rendkívül jó alkalmazkodó képességgel rendelkező hal megszökött a telepekről és kikerült a természetbe. Magyarországon ezután gyorsan elterjedt, a folyók lassúbb szakaszain sokfelé megtalálható és több állóvízben is előfordul önfenntartó állománya. Nagyon hasonlít hozzá a törpeharcsa, de annak az oldalát nagyobb, szabálytalan foltok tarkítják. Az úszóikban a sugarak és az úszóhártya színe nem különbözik egymástól, de további különbség, hogy a mellúszó tüskéjének belső éle sima. A színezetük is eltérő, hiszen a törpe inkább barna, míg a fekete törpeharcsa sötétebb. Színezete élőhelytől függően változó, az aranyló olajzöldtől egészen a feketéig terjed. Általában felül sötétebb, míg alul a hasoldalán sárgás. Széles, lapos fején az alsó ajak nem ér a felső ajkon túl. Alsó és felső állkapcsán 4-4, összesen 8 bajuszszálat visel. A felső állkapcsán lévőkből 2 bajuszszál a szeme és az orrnyílása között, a két leghosszabb pedig a szájszögletében helyezkedik el. Úszói szúrnak, hiszen hátúszója elején egy igen hegyes, kemény csonttüske van, és ehhez hasonló csonttüskét visel a mellúszók elején is. Bőre pikkelytelen, az oldalvonala végighúzódik a testén. A fekete törpeharcsa, mint a többi törpeharcsaféle, képes akár oxigénben szegény vízben is élni. Tűri mind a brakkvízet és mind a nagyon meleg vizet is. Jellemző magyarországi élőhelye a növényzettel benőtt, változó vízjárású lassú vízfolyások, alföldi csatornák, vagy az olyan állóvizek mint a zárt holtágak és a halastavak. A kifejlett példányok inkább éjjel aktívak, azonban a fiatalok nappal is táplálkoznak. A fekete törpeharcsa mindenevő falánk hal. Mindent felfal, ami számára ehető a magoktól és rovaroktól kezdve a döglött vagy élő halakig és rákokig. Szaporodásbiológiája hasonló, mint a törpeharcsáé, de ívása júniusban magasabb hőmérsékleten történik. Sok helyre szándékosan betelepítették, mert jól alkalmazkodó halfaj. A sporthorgászok szívesen fogják, hiszen gyorsan lehet sokat fogni belőlük. Hátrányt jelenthet azonban a horgászatban, ha a cél más nagyobb halfajok, hiszen tömeges előfordulása esetén akár ezt meg is akadályozhatja. Igen mohó és intenzív a kapása, a kisebb horgot képes nagyon gyorsan lenyelni. Fogása nincs korlátozva, a kifogott ugrándozó kisméretű példányokkal vigyázni kell, mert úszóin lévő tüskéi szúrósak, ezért célszerű kesztyűvel megfogni őket. Sok helyen dobvarsa csapdákkal irtják. Húsa világos színű és jó ízű, de nyáron, ha nincs jól tárolva, hamar megromolhat."),
            new ClassHal("Feketeszájú géb", "Neogobius melanostomus", R.drawable.feketeszaju_geb, "Idegenhonos faj", null, 0, 0,"A feketeszájú géb elterjedési területe a Fekete- és Azovi-tenger teljes parti szakasza, ahonnan a folyókba is felúszik. A Kaszpi-tenger nyugati felén a parti övben megtalálható. Betelepítették az Aral-tóba és Európa több folyójába bekerült, így megjelent a Dunában is. Magyarországon 2001-ben azonosították az első példányát, amelyet Gödnél fogtak. 2004-ben Észak-Amerikába is eljutott, a hajók ballasztvizeinek köszönhetően. A Neogobius halnem többi fajától megkülönböztethető amiatt, hogy azok az elülső hátúszójuk végén nem viselnek fehérrel szegett fekete foltot. Hasonlít rá még a tarka géb , de annak a szája kisebb, és felső állkapcsán az elülső orrnyílások csőszerűen előrenyúlnak. Az amurgéb, a botos és a cifra kölönte hasúszói különállóak, nem alkotnak tapadókorongot. Tömzsi teste hosszúkás, hengeres és a farokrészen oldalról lapított. Kis méretű hal a testhossza 15-18 centiméter, de legfeljebb 25 centiméter. 4 centiméteresen már felnőttnek számít. 31-34 csigolyája van. Feje testéhez képest nagy és a szeme között nincs pikkely. Ezzel szemben a tarkója, a háta, a mellúszók tövi része, a kopoltyúfedők felső egynegyede, a torka és a hasa is pikkelyezett. 45-58 vagy többnyire 47-54 pikkelye van egy hosszanti sorban, melyek nagyságukat tekintve a rokon fajokéhoz képest nagyobbak. A fenéklakó életmód miatt a hasúszói összenövésével kialakult tapadókorongja, a has hosszának 60-80 százalékát teszi ki. Két hátúszója közel helyezkednek el, az első 6 tüskés sugárral, a második 1 tüskés és 11-17 (többnyire 14-16) lágy sugárral. A hátúszói egyforma magasak, míg a farok alatti úszójában 1 tüskés és 10-13 lágy sugár található. A fiatalok színe palaszürke ám a felnőtt példányok szürkésbarnák és az alapszínét szabálytalan sötétbarna vagy fekete foltok tarkítják. A fajra jellemző specifikum, hogy a zöldesen mintázott első hátúszójának végén egy világossal szegett fekete folt látható. A nászruhás hímek színe megváltozik és iváskor feketére vált. A sekély vizű, napfényes, félsós tengerparti lagúnák a kedvenc helyei, ahol gyorsan fejlődik. De a tengerbe ömlő folyóvizekben is egyre feljebb nyomul. Fenéklakó hal, a víz oxigén- és sótartalmának változásait, valamint a hőmérséklet ingadozását jól tűri. A 4-20 Celsius-fokos hőmérsékletet kedveli, de a 30 Celsius-fokot is megtűri. Tápláléka állati eredetű, melyet elsősorban puhatestűek, férgek, csigák, rákok és halikra alkot. Legfeljebb 4 évig él. Ivarérettségét 1-2 éves korára éri el. Április - szeptemberben ívik édes- és brakkvízben egyaránt. A nőstény az ikrát a hím által tisztára sepert mélyedésbe rakja, melyek jól tapadnak. A lerakott ikra száma ezer körüli, melyekből kikelt ivadékokról a hímek egy ideig gondoskodnak."),
            new ClassHal("Felpillantó küllő", "Romanogobio uranoscopus", R.drawable.felpillanto_kullo, "Védett faj", null, 0, 0,"A Duna vízgyűjtő medencéjének folyóvizeiben élő kis termetű gazdasági szempontból jelentéktelen hal, azonban nagy természeti értéket hordoz, és egyben a legritkábban előforduló magyarországi küllőfaj. A felpillantó küllő a Duna vízrendszerének sebes sodrású, nagy és hűvös víztömegű, kavicspadokkal tarkált oxigéndús vizeiben él. Kisebb vízhozamú folyókban és patakokban, valamint a nagyobb folyók lassabb szakaszain és a tavakban nem él meg. Magyarország folyóinak erős sodrású szakaszain a pérzóna alsó régiójában és a paduczónában jellemző, de itt is csak ritkán található meg. Mivel nagyon ritka faj, ezért nagy jelentőségű az esetleges felbukkanása, melyre a Dráva a Mura és a Tisza folyókban lehet elsődlegesen számítani. Az egyik megbízható előfordulását a Tisza magyarországi felső szakaszáról, Tiszacsécséről jelezték. Közeli rokonaival a fenékjáró küllővel, a halványfoltú küllővel és a homoki küllővel téveszthető össze legkönnyebben. Rokonaitól a felpillantó küllő teste nyúlánkabb, bajuszai hosszabbak és szemei magasabban és a keskenyebb homlok felső oldalán ferdén állnak. A fenékjáró küllő erősen pettyezett hát- és farokúszója, valamint magas faroknyele miatt különböztető meg. A halványfoltú küllő faroknyele magas, és végbélnyílása a hasúszók alapjához esik közelebb. A homoki küllő teste nyújtottabb, háta alacsony, hátúszójában pedig 8 osztott sugár található, és a végbélnyílása a hasúszók tövéhez van közelebb. Az alak és szájforma tekintetében ugyancsak hasonló a márna és a petényi-márna, de őket két pár bajuszuk jól megkülönbözteti a csupán egy pár bajuszt viselő küllőktől. Teste orsószerűen nyúlt, melyhez a fejformája hosszúkás és lapított. A szeme viszonylag kicsi és a fejtetőre tolódott, felfelé tekintő ezért is nevezik felpillantónak. A hal testhossza legfeljebb 10 centiméter. 40-42 nagy pikkelye van az oldalvonala mentén. Torka pikkelyezett a hasa fehér. A felső állkapocs hátulsó peremén mindkét oldalon egy-egy bajuszszál van. A hátúszója és a farokalatti úszója is rövid és magas. A farokúszó hosszú és erősen kimetszett. Az úszók áttetszőek, színtelenek vagy halványsárga árnyalatúak. Kis rajokban élő fenékhal. Mivel a mederfenéken tartózkodik, onnan szerzi táplálékát, mely algaszövedékek, apró férgek, rovarlárvák, planktonrákok és a folyó által görgetett egyéb szerves hulladékokból áll."),
            new ClassHal("Fenékjáró küllő", "Gobio gobio", R.drawable.fenekjaro_kullo, "Védett faj", null, 0, 0,"A fenékjáró küllő egész Európában előfordul, de folyamatos elterjedéséről nem beszélhetünk. Állományai szigetszerűen előfordulnak a magyarországi folyókban, patakokban, homokos vagy kavicsos aljzatú állóvizekben, csatornákban. Viszonylag gyakori, de visszaszorulóban lévő faj. Franciaországtól a Baltikumig, valamint Ukrajnáig sokfelé megtalálható. A hal teste megnyúlt, oldalról és a hasi részen kissé lapított csaknem hengeres, faroknyele rövid, magas. Feje rövid, zömök, viszonylag magas szemei közepes nagyságúak és a fejtetőhöz közel ülnek, orra tömpe, szája alsó állású, vaskos.Szájszegletében 1-1 rövid bajuszszál van, amelyek visszahajtva legfeljebb a szem közepéig érnek. Pikkelyei nagyok, puhák, számuk 40-45 az oldalvonal mentén. Hátúszója 9-10, farok alatti úszója 8-9 sugarú; farokúszója villás. Garatfogai kétsorosak (2)2.5-5.3(2). A fenékjáró küllő átlagos testhossza 8-14 centiméter, legfeljebb 21 centiméter. 39-41 csigolyája van. E halfaj háta feketés, zöldes vagy kékes árnyalatú barna; oldalai szürkéssárgák enyhe ezüstös csillogással, az oldalvonal mentén 6-14 sötét vagy fekete folttal; hasa csillogóan fehér. Páratlan úszóin és a farok alatti úszón sötét pettyek vannak, amelyek csak erre a küllőfajra jellemzőek. A hal a patakok, folyók gyors és lassú folyású részeit kedveli, de előfordul a homokos vagy kavicsos medrű, oxigéndús tavak partjánál is. Rajban élő fenékhal, a nyári hónapokban a sekély vizekbe, télen a mélyebb részekbe húzódik. Tápláléka férgek, rákok, rovarlárvák, alkalmilag halikra is. Ivarérettségét 2-3 éves korában éri el.Ívási időszaka április-júniusra esik, ilyenkor a hímek fején és testük elülső részén nászkiütések jelennek meg. A nőstény ragadós, körülbelül 1.5 milliméter átmérőjű ikráit a sekély, elöntött részeken kövekre vagy növényekre rakja. Egy nőstény 1000-3000 ikrát rak. A kelési idő 3-5 nap. A kikelő lárvák 5-6 milliméter hosszúak."),
            new ClassHal("Fogassüllő", "Sander lucioperca", R.drawable.fogassullo, "Őshonos, fogható faj", "03.01.-04.30.", 30, 0,"A nem túl gyors áramlású folyószakaszokon és az oxigénnel jól ellátott állóvizekben is jól érzi magát. Legnagyobb sügérfélénk, a nagyobb példányok 50-60 cm hosszúak, de 1 méternél és akár 15 kg-nál nagyobbra is megnőhet. Teste erősen megnyúlt, oldalról enyhén lapított. Aránylag alacsony hátú hal. Nagy feje ugyancsak oldalról lapított. Csúcsba nyíló szája is nagy, szájában erős fogak ülnek, közülük a többi közül kiemelkedő, az alsó és felső állkapocs csúcsánál található, ún. ebfogak azok, amelyek a fogassüllő és a kősüllő kifejlett példányait egymástól jól megkülönböztethetővé teszik. Fésűs pikkelyei aprók, színük a háton sötét zöldesszürke, az oldalakon ezüstösfehér alapúak. A hátvonaltól a test közepéig 8-12 szabálytalan alakú és elrendeződésű, sötét, ún. harántsáv húzódik. Táplálékát – a fiatal példányok zooplankton étrendjét kivéve – szinte teljes egészében a halak teszik ki. Elsősorban éjszaka jár aktívan táplálék után, nappal inkább csak búvóhelye közelében zsákmányol. Március-áprilisban szaporodik. A tejesek a kemény aljzatú részeken fészeknek alkalmas felületet (növényzet, gyökerek) tisztogatják meg az üledéktől, az ikrások erre rakják ikrájukat, amelyet a megtermékenyülés után kikelésig őriznek."),
            new ClassHal("Folyami géb", "Neogobius fluviatilis", R.drawable.folyami_geb, "Idegenhonos faj", null, 0, 0,"A folyami géb a Fekete-, az Azovi- és a Márvány-tenger sekély partmenti részein él, ahonnan messzire felúszik a nagy folyókba. A Balatonban 1970 óta tömeges. Azóta Magyarországon néhány tóban és folyószakaszon is kialakultak állományai. Mára a magyarországi felső Duna szakaszon tömegesen fordul elő. 1997-ben elérte a lengyelországi Visztula folyó középső szakaszát. Az általa újonnan meghódított helyeken, inváziós fajnak bizonyult. A hal testhossza 15-18 centiméter, legfeljebb 25 centiméter. 58-65 pikkelye van egy hosszanti sorban. Tarkója pikkelyezett; 26 pikkely található a fej és a hátúszó töve között. Első hátúszója tüskés sugarú. Mérsékelt övi gébféle, amely egyaránt megél az édes- és brakkvízben is. A 4-20 Celsius-fokos hőmérsékletet kedveli. Megtalálható a folyókban, ezek torkolatvidékén, valamint a tavakban, lagúnákban és patakokban is. A homokos és iszapos medreket választja élőhelyül. Tápláléka főként apró rákok, puhatestűek és kishalak, valamint a fenéken élő apró állati szervezetek. Legfeljebb 5 évig él. Kétévesen válik ivaréretté. Az ívási időszak április–július között van, de helyenként, ha a hőmérséklet nem csökken 13 Celsius-fok alá, akár szeptemberig is eltarthat. Ebben az időszakban a nőstény több alkalommal is ívhat. A hím ekkortájt fekete színt ölt, úszói pedig sárga szélűek lesznek, továbbá és megnövekednek az állkapcsai, ami a területének védelmezését segíti.[1] Az íváshoz a hím vájja ki, a törmelékben a fészket, aztán pedig őrzi és gondozza az ikrákat."),
            new ClassHal("Fürge cselle", "Phoxinus phoxinus", R.drawable.furge_cselle, "Védett faj", null, 0, 0,"Az áramvonalas, orsószerű fürge cselle egy kistermetű, pataklakó halfaj. Lekerekített, tompa orra, nagyon apró pikkelyei, valamint az oldalát díszítő, olykor sötétebb, máskor világosabb, de azért észrevehető foltsor minden más halunktól megkülönbözteti. Ezekre figyelve más hazai fajjal nem téveszthető össze."),
            new ClassHal("Garda", "Pelecus cultratus", R.drawable.garda, "Őshonos, fogható faj", "04.15.-05.31.", 20, 0,"Egykor vándorló halfaj volt hal, amely a tengerbe torkolló folyók édesvizében szaporodik. A tengertől távolra kerülő populációk azonban felhagytak a vándorlással, alkalmazkodtak az édesvízi életmódhoz. A hazai állomány főként nagy folyóinkban, illetve nagyobb tavainkban él. Testhossza 25-35 centiméter, maximum 60 centiméter. Teste megnyúlt, oldalról erősen lapított. Hasvonala domborúan ívelt, háta viszont majdnem egyenes. Feje aránylag kicsi, orra rövid és hegyes, a szeme nagy. Szája felső állású, szájrése meredeken felfelé irányul. Pikkelyei nagyon aprók, számuk a hullámos oldalvonalon 90-115 darab közé tehető. Mellúszói feltűnően nagyok, túlérnek a hasúszók tövén. Rajhal, amely napközben a fenék közelében tartózkodik, éjszaka a felszínre emelkedik. Tápláléka planktonrákok, rovarlárvák, rovarok és apró halak. Május-júliusban ívik a vizek mérsékelt áramlású mélyebb részein. Egyetlen őshonos halfajunk, amelyik lebegő ikrával szaporodik."),
            new ClassHal("Gyöngyös koncér", "Rutilus frisii", R.drawable.gyongyos_koncer, "Védett faj", null, 0, 0, "Európában ritka és veszélyeztetett kategóriába sorolt az állománya, Magyarországon csak elvétve az idevetődő példányokkal lehet találkozni. Gazdasági jelentősége nincs, természeti jelentőségét az adja, hogy a Felső-Duna medencéjének bennszülött faja. A gyöngyös koncér élőhelye a felső Duna-szakasz néhány tava, valamint ezek be- és kifolyó vizei Bajorországban (Chiem-tó), Ausztriában (Traun, Attersee és Mondsee). A törzsalak Rutilus frisii frisii mint vándorhal a Fekete-tenger északnyugati felének brakkvizeiben él, és ívás idején (március–április) a beömlő folyókba (Dnyeszter, Nyugati-Bug, Dnyeper, Don, Rion, Batum) úszik fel. A Kaszpi-tengertől északra lévő területeken, valamint Anatóliában is előfordul. Legkönnyebben a nyúldomolykóval és a fejes domolykóval lehet összetéveszteni, de az oldalvonalon található pikkelyszámuk eltér. Közeli rokona a leánykoncér oldalvonala mentén is kevesebb, 44–49 pikkely számolható. A paduc a szilvaorrú keszeg és az állas küsz pikkelyszáma ugyan hasonló, de a paducnak teljesen alsó állású a szája, a szilvaorrú keszegnek pedig az anális úszója lényegesen hosszabb és az állas küsz szája felső állású. A hal teste orsó alakú, keresztmetszetében csaknem kerekded, feje kicsi. Lekerekített orra túlnyúlik a kissé ferdén álló kis szájnyíláson. Nagy, kerekded pikkelyei vannak. 62-67 az oldalvonal mentén. Mellúszói 17-18, hátúszója 11-12, farok alatti úszója 12-14 sugarú. Garatfogai egysorosak, 6(5)-5. Háta feketés zöld, oldalai világosabbak, hasa ezüstösen csillogó. Hát-, farok- és mellúszói szürkék, a hasúszók és a farok alatti úszó világos pirosak. Hasoldala ívás idején vörhenyesen csillogó. Testhossza 40-60 centiméter, legfeljebb 70 centiméter és testtömege elérheti a 8 kilogrammot. Kisebb csapatokban a mederfenéken keresi táplálékát, mely gerinctelen szervezetek, mint talajférgek, apró rákok, rovar, lárvák, szúnyoglárvák és puhatestűek. Előfordul, hogy kisebb halakat és vízinövényeket is fogyaszt. Legfeljebb 12 évig él. Mélyvízi hal, rendszerint távol marad a partoktól és a folyóvizek felső szakaszának kavicsos aljzatán ívik. Élőhelytől függően március–május között az ivarérett példányok a tavakba ömlő folyókba vagy a levezető csatornákba úsznak, esetleg a sekély vizű partszakaszokat keresik fel. A hímek fején és hátán nászkiütések jelennek meg. Ívásához ideális a 12-19 Celsius-fokos vízhőmérséklet. A nőstények által érlelt ikra mennyisége testtömeg-kilogrammonként 32-35 ezer, az ikraszemek átmérője 1,9-2,1 milliméter között változik.Az ivadék 10-16 nap múlva kel ki az irkából. A gyöngyös koncért ipari mértékben halásszák. A halat tenyésztik is."),
            new ClassHal("Halványfoltú küllő", "Romanogobio albipinnatus", R.drawable.halvanyfoltu_kullo, "Védett faj", null, 0, 0,"Magyarországon elsősorban a ragadozó halak étrendjének bővítése miatt jelentős. Védettségét elsősorban a szűk elterjedési terület indokolja, mert élőhelyein gyakori és szaporodóban lévő hala vizeinknek. Közép- és Kelet-Európában, jobbára a Fekete- és a Kaszpi-tengerbe ömlő folyók vízrendszerében él. Magyarországi vizekben őshonos és gyakori a kavicsos medrű hegyi patakokban, a nagyobb folyók felső szakaszán a forrásvidékig fordul elő. Legkönnyebben a fenékjáró küllővel, a felpillantó küllővel és a homoki küllővel téveszthető össze. A fenékjáró küllőtől az erősen pettyezett hát- és farokúszója, valamint a hasúszók tövétől távolabb eső végbélnyílása különbözteti meg. A felpillantó küllő végbélnyílása távolabb van a hasúszók tövétől, és a faroknyele hengeres. A homoki küllő teste nyújtottabb, háta alacsony, faroknyele hengeres, és hátúszójában 8 osztott sugár van. Más fajok közül a rózsás márna vagy a petényi-márna hasonlít rá a legjobban, de itt egyértelmű különbséget jelent, hogy a márnák 2 pár bajuszt viselnek a szájuk körül, a hátúszójukból hiányzik a tövis, nagyobbak a pikkelyeik és minden oldalon két sorban 3 vagy 2 és 5 számú, kampós garatfogaik vannak. A hal testhossza 8-10 centiméter, legfeljebb 15 centiméter. 39-43 nagy pikkelye van az oldalvonala mentén. A feje kúpos, orra lekerekített, a szemei nagyok és fölfelé tekintenek. Alsó állású szája szögletében 1-1 hosszabb bajuszszál található. Végbélnyílása közelebb esik a hasúszók tövéhez, mint a farkalatti úszó kezdetéhez. Az oldalvonal és a hasúszók tövi része között 4 pikkelysor található. A felső állkapocs hátulsó peremén mindkét oldalon egy-egy bajuszszál van. Rajban élő fenékhal. Nagyobb folyókban a paduczónától a torkolatig egyaránt megtalálható és megél az állóvizekben is. Legnépesebb állományai a dévérzónában alakulnak ki, ennek a jellemző hala. Hegy- és dombvidéki patakjainkban alkalmanként megtalálható, a kisebb folyóinkban elsősorban a sügérzónában lelhető fel. Tápláléka fenéklakó állatok és algák, de növényi anyagot és szerves törmeléket is fogyaszt."),
            new ClassHal("Harcsa", "Silurus glanis", R.drawable.harcsa, "Őshonos, fogható faj", "05.02.-06.15.", 60, 100, "A magyarországi halfauna „óriása\", testhossza a 2,5 métert is elérheti, tömege meghaladhatja a 100 kg-ot. Megtalálható minden olyan folyóban és állóvízben, ahol előfordulnak olyan mély, gödrös részek, bedőlt fák, kőrakások amelyek árnyékában biztosan rejtőzhet. A teste a többi hazai halunktól különbözik. Hosszúra nyúlt, a fején fölülről, a farokrészén oldalról erősen lapított, pikkelytelen testű hal. A test törzsrésze rövid, hengeres, az izmos farokrész igen hosszú. Feje nagy és széles, szeme kicsi. Nagy, félig fölső állású száján a felső ajakon, a szájszeglet közelében 2 hosszú, alul 4 rövidebb bajuszszálat találunk. Szélesre nyíló szájában felül és alul rendkívül sok apró, kissé visszahajló, tűhegyes ránőtt fog helyezkedik el (ez az ún. gerebenfogazat). Teste teljesen pikkelytelen, oldalvonala végig jól látható. Színe a világosabb zöldesbarnától a feketéig terjedhet, rendszerint márványozott. Rövid hátúszója szokatlanul elöl helyezkedik el, és mindössze 2-4 osztott sugár található benne. Farokalatti úszója viszont nagyon hosszú, a végbélnyílástól egészen a farokúszó tövéig tart. Mellúszójának első sugara vaskos, kemény, tompa végű csonttüske. Főként éjjel aktív, rendkívül falánk ragadozó. Kisebb példányai főként hallal, rákokkal, piócákkal, puhatestűekkel táplálkoznak, a nagyobbak tápláléka elsősorban halakból áll, de kétéltűeket, kisemlősöket és néha még vízimadarakat is zsákmányolnak. A harcsa ívása az egyenletesen meleg május végi, június eleji időszakra esik, amikor a víz hőmérséklete már tartósan 20 fok körüli. Az ikrákat növények vízbe nyúló gyökérzetére vagy az áradás által elöntött növényzetre rakják, amit később is őriznek."),
            new ClassHal("Homoki küllő", "Romanogobio kesslerii", R.drawable.homoki_kullo, "Védett faj", null, 0, 0, "A Duna vízrendszerének nagy természeti értéket hordozó, de gazdasági szempontból jelentéktelen halfaja. Egyre gyérülő állománya Magyarországon még helyenként jelentős előfordulással bír, azonban védelme ennek ellenére is indokolt. A faj a Duna középső és alsó szakaszának mellékfolyóiban él. Magyarországon helyenként gyakori is lehet és megtalálható Délnyugat-Oroszországban és a lengyelországi Visztula felső szakaszán. Közeli rokonaival a fenékjáró küllővel, a halványfoltú küllővel és a felpillantó küllővel téveszthető össze legkönnyebben. Az 1980-as évekig Magyarországon előkerült példányait például felpillantó küllőnek vélték. A fenékjáró küllőt erősen pettyezett hát- és farokúszója, valamint magas faroknyele különbözteti meg. A halványfoltú küllő háta és faroknyele is magasabb, emellett a hátúszójában rendszerint csak 7 osztott sugár van. A felpillantó küllő hátúszójában is csak 7 elágazó sugár van, végbélnyílása pedig inkább az anális úszó kezdetéhez esik közelebb, mint a hasúszók tövéhez. Az alak és szájforma tekintetében ugyancsak hasonló a márna és a petényi-márna, de őket két pár bajuszuk jól megkülönbözteti a csupán egy pár bajuszt viselő küllőktől. Testformája hosszúkás, hengeres, nem annyira magas, mint inkább széles. Testhossza 10-12 centiméter, legfeljebb 13 centiméter. Feje közepesen nagy, orra tompán lekerekített, 40-43 nagy pikkelye van az oldalvonala mentén. Az oldalvonal és a hasúszók tövi része között 3-4 pikkelysor található. Az oldalvonal mentén sorban 6-10, leggyakrabban 8 sötét folt van. A hátúszó közepes hosszúságú, kezdete a hasúszók tövével egy vonalban vagy kissé előbbre helyezkedik el. A farok alatti úszó rövid és magas. A faroknyél hosszú, alacsony, hengeres, magassága és szélessége csaknem azonos. A farokúszó hosszú, erősen kivágott. Szája alsó állású, húsos ajak veszi körül és a felső állkapocs hátulsó peremén mindkét oldalon egy-egy bajuszszál található. A torokrész és a mell pikkelytelen és ennek nagysága alapján több alfaját írták le. Jellegzetes élőhelyei elsősorban a domb- és síkvidéki folyók homokos, kavicsos szakaszai, de előfordul a hegyvidéki zóna patakjaiban is. Apró termetű rajhal, amely a gyors sodrású, oxigéndús vizeket kedveli. A mederfenéken tartózkodik, ahol tápláléka a talaj algaszövedéke, apró férgek, rovarlárvák és planktonrákok. Legfeljebb 5 évig él."),
            new ClassHal("Jászkeszeg", "Leuciscus idus", R.drawable.jaszkeszeg, "Őshonos, fogható faj", "04.15.-05.31.", 20, 0, "A folyók jellemző, mérsékelten magas hátú, oldalról lapított testű keszegféléje. A kifejlett példányok testhossza átlagosan 30-50 centiméter. Az idősebb egyedek mell-, a has- és a farokalatti úszói vörösek, hát- és farokúszói pedig barnásak. Feje és szeme közepes nagyságú, szemgyűrűje nem színes. Szája csúcsba nyíló, szájszöglete nem éri el a szem elejének vonalát. Pikkelyei aprók, számuk az oldalvonal mentén 55-60. Rajhal, nyáron gyakran tartózkodik a felszín közelében is, télen a mederfenékre húzódik. A fiatal példányok állati és növényi planktonnal táplálkoznak, míg a felnőttek férgeket, apró rákokat, rovarlárvákat és kisebb puhatestűeket esznek, a nagy példányok alkalmanként halivadékot is fogyasztanak. Április és június között ívik a sekély szélvizekben. Ikráját kavicsra vagy vízinövényekre rakja."),
            new ClassHal("Karikakeszeg", "Blicca bjoerkna", R.drawable.jaszkeszeg, "Őshonos, fogható faj", null, 0, 0, "Kitűnő alkalmazkodóképességét mutatja, hogy a patakoktól kezdve a nagy folyókig szinte minden víztérben megtalálja életfeltételeit, de jellemzően a folyók középső és alsó szakaszának a hala. A dévérkeszeg mellett a másik leggyakrabban előforduló keszegfajunk. Teste oldalról lapított. Feje kicsi, orra rövid és tompán lekerekített. Kicsi szája csúcsba nyíló vagy félig alsó állású. Szemei a fejéhez képes nagyok. A mell- és a hasúszók rövidek, tövük gyakran vörhenyes. A mellúszó nem éri el a hasúszók kezdetének vonalát. A háta sötétbarna, esetleg kékes színezetű, az oldalak szürkésen ezüstös csillogásúak, a hasa ezüstös vörhenyes. Leginkább a dévérkeszeggel téveszthető össze, de amíg a karikakeszeg oldalvonala fölött csak 8–10 pikkelysor húzódik, a dévérkeszegnél 11–13. A pikkelyek mérete az oldalvonaltól a hát íve felé haladva alig csökken. Elsősorban apró méretű állatokkal (rovarok, lárvák) táplálkozik, de előszeretettel fogyaszt különféle növényi eredetű táplálékot is. Áprilisban kezdődő ívása egészen júniusig elhúzódhat, ikráit sekély vízben növényekre rakja."),
            new ClassHal("Kaukázusi törpegéb", "Knipowitschia caucasica", R.drawable.kaukazusi_torpegeb, "Idegenhonos faj", null, 0, 0, "A kaukázusi törpegéb a sekély partszakaszok brakkvizeiben él, de a partközeli tavakban és a folyók alsó szakaszain is megtalálható. A hal testhossza 2 - 3 centiméter, legfeljebb 5 centiméter. 30 - 37, többnyire 32 - 36 pikkelye van a hosszanti sorban. Hátúszóján 7 tüske és 7-8 sugár, míg farok alatti úszóján 1 tüske és 7-9 sugár van. 31-32 csigolyája van. A fejtető, a kopoltyúfedők és a mellúszók tövi része pikkelyek nélküli. A hím ívás idején világosbarna, oldalán 4-5 élénk, feketés keresztsávokkal. Fenékhal, amely apró talajállatokkal táplálkozik."),
            new ClassHal("Kecsege", "Acipenser ruthenus", R.drawable.kecsege, "Őshonos, időszakos felmentéssel fogható faj", null, 0, 0, "A leggyakoribb tokfélénk, egykor folyóink halászatában komoly jelentőséggel bírt, de az utóbbi időben állománya megritkult a természetes vizeinkben, ezért nem fogható, de nem védett halfaj. A nagyobb folyók gyorsabb és mély vizű, kavicsos-homokos vagy agyagos szakaszait, mederrészeit kedveli. A tavakban és a duzzasztott folyók üledékes mederszakaszain megél ugyan, de nem szaporodik. Testhossza az 1 métert, testtömege 6-7 kg-ot is elérheti. A kecsege orsó alakú, megnyúlt testű hal, orra hosszú, fölfelé hajló. Hátán és hasán 12-18 csontvért található. Alsó állású szája kicsi, alsó ajka középen megszakított. Négy rojtozott bajuszszála viszonylag hosszú, lesimítva eléri a felső ajkat (ez alapján mindegyik védett tokfajunktól és az idegenhonos lénai toktól is elkülöníthető). Hátúszója a test végéhez közel esik. Testének nagyobb része sötétbarna, szürkésbarna vagy feketésbarna, esetleg zöldes vagy kékes árnyalattal. A hasa sárga vagy vörhenyes fehér színű. A sötét alapszínű úszókat rendszerint egy keskeny fehéres sáv szegélyezi. Ívási ideje áprilistól júniusig tartó hónapokra esik, a folyók mélyebb, sóderes és homokos szakaszain. Férgekből, csigákból, rovarlárvákból álló táplálékát a mederfenéken keresi. Ahol a tiszavirág lárvája bőséggel található, ott ez alkotja a fő táplálékát."),
            new ClassHal("Kessler-géb", "Ponticola kessleri", R.drawable.kessler_geb, "Idegenhonos faj", null, 0, 0, "Köves vagy kemény homoktalajú parti vizekben él, ahonnét a folyókba úszik. Újabban a Dunán egyre feljebb terjeszkedik. 1996-ban eljutott Bécsig. A Dunán keresztül a Rajna folyóba is bejutott, ahol inváziós fajnak bizonyult. A Fekete-tenger partjain, levő tavakban és folyótorkolatokban, majdnem mindenütt megtalálható, kivéve az Azovi-tengert és annak vidékét. Nagyobb folyók, amelyekbe megtalálható a Kessler-géb: Duna, Dnyeper, Dnyeszter és Volga. A Kaszpi-tenger mindegyik partjánál is vannak állományai. A hal testhossza 12-18 centiméter, legfeljebb 22 centiméter. 64-79 pikkelye van egy hosszanti sorban. A fejtető és a kopoltyúfedők egyharmada pikkelyezett. Mérsékelt övi gébféle, amely egyaránt megél az édes- és brakkvízben is. A 4-20 Celsius-fokos hőmérsékletet és az alacsony sótartalmú vizet kedveli. A dús vízinövényzetű és a törmelékes aljzatot választja élőhelyül. A hím területvédő. Tápláléka férgek, apró rákok, puhatestűek és kishalak. Kétévesen ívik először. Március-májusban van az ívási időszaka. A hím őrzi és gondozza az ikrákat. A ragadós ikrák a vízinövényeken és törmelékeken vannak elhelyezve."),
            new ClassHal("Kurta baing", "Leucaspius delineatus", R.drawable.kurta_baing, "Védett faj", null, 0, 0, "Az apró kurta baing felső állású szája meglehetősen nagy, de mivel szájhasítéka meredeken felfelé irányul, szájszöglete nem éri el a szem vonalát. A faj egyik legjellemzőbb bélyege, hogy oldalvonala rövid, csupán az első 7-12 pikkelyen látható, azonban több pontyféle ivadékával is összetéveszthető, mivel azok fiatal példányain az oldalvonal gyakran nem látszik végig. A szélhajtó küsz teste oldalról lapítottabb, farokalatti úszója pedig hosszabb, mint a kurta baingnál. A balin szájhasítéka csak enyhén irányul fölfelé, szöglete viszont a szem alá ér. A domolykó szája csúcsba nyíló, farokalatti úszója pedig rövidebb."),
            new ClassHal("Kövi csík", "Barbatula barbatula", R.drawable.kovicsik, "Védett faj", null, 0, 0, "A pisztráng- és a pérzónában a tőle sokkal értékesebb ragadozók tápláléka, ezért ilyen vizekben gazdaságilag is jelentős szerepet játszik. A magyarországi patakokban is gyakori, de mivel igazi pisztrángos víz alig található nálunk ezért kevésbé jelentős, védettsége az állomány megóvását szolgálja. Európa és Szibéria folyóinak és patakjainak gyors folyású, tiszta vizű, homokos, kavicsos aljzatú részein él. A nappalt a kövek alatt, fedett mélyedésekben tölti, főleg éjszaka mozog. Magyarországon is őshonos és a hazai vizekben még gyakori. Védettsége elsősorban megelőző jelleggel a meglévő állomány megóvását szolgálja. Nagyobb folyókban a pisztrángzónától a paduczóna aljáig található meg ettől lejjebb már csak véletlenszerűen. Általánosan elterjedt és gyakori faj a középhegységi és dombvidéki patakokban, melyek többnyire a domolykó zónába sorolhatók. Előfordul a Nyitrában, a Szávában, a Balatonban, a Poprádban, a Tarcában, a Vágban, a Garamban, a felső Tiszában és a felső Körösben, a Csernában, a Bódvában, a Berettyóban, az Ipolyban, a Küküllőben, az Oltban és a Sajóban és még további folyóvizekben. A réti csíktól eltérően ugyanis a kövi csík, ha nem is kizáróan, de leginkább folyóvízben tartózkodik. Hasonló fajok a csíkfélék és felületes szemlélő összetévesztheti még az fiatal angolnával, de az nyúlánkabb és a hasúszói hiányoznak. A közeli rokon réti csík oldalát hosszanti csíkok borítják ezért jól megkülönböztethető a foltos kövitől. A vágó csík és a kőfúró csík feje és teste lapított és a szemük hátrafelé áll, valamint felmereszthető a csonttüskéjük. Pikkelyképlete nincs, mert pikkelyei alig láthatók és elszórtan helyezkednek el. Oldalvonala ellenben végig jól látható, majdnem egyenes lefutású. Teste erősen megnyúlt, hengeres. Feje oldalról lapított, az orr irányába elkeskenyedik. Szája alsó állású, a felső ajkán négy, a szájszöglet felett két bajuszszállal. Háta szürkésbarna, oldala szennyes sárga, szürke márványozottsággal. A hasa szennyes szürke. Kis termetű hal testhossza legfeljebb 10-15 centiméter. Vegyes táplálékon él, egyaránt fogyaszt apró fenéklakó állatokat víziférgeket, rovarokat, azok lárváit, halikrát, különböző növényi részeket és szerves törmeléket fogyaszt."),
            new ClassHal("Kősüllő", "Sander volgensis", R.drawable.kosullo, "Őshonos, fogható faj", "03.01.-06.30.", 25, 0, "Elsősorban a folyók középső-alsó szakaszán, valamint a nagyobb állóvizek egy részében található meg. Az elmocsarasodott, sok lebegő iszapszemcsét tartalmazó és kisebb vizekben nem él. A nagyobb egyedek testhossza 25-30 cm, ritkán 40 cm-nél nagyobb méretet is elérhet. Megnyúlt, aránylag alacsony, oldalról lapított testű ragadozóhal. Feje hosszú, a szeme nagy, szája csúcsba nyíló. , Szájában erős fogak ülnek, de a többi közül kiemelkedő ebfogai csak a 10-15 centinél kisebb, fiatal példányoknak vannak. Farokúszója jól fejlett, a széle enyhén bemetszett. Farokalatti úszója rövid. Pikkelyei aprók. Zöldesszürke oldalát határozottabb vonalú sötét harántsávok tarkítják. Legjobban rokona, a süllő hasonlít hozzá, de annak orra és teste nyújtottabb, oldalainak harántsávozása szabálytalanabb és elmosódottabb, a szája nagyobb, és a kifejlett példányoknak ebfoga van. Kezdetben zooplanktonnal, majd fenéklakó gerinctelenekkel táplálkozik, az idősebbek fokozatosan áttérnek a ragadozó életmódra, apró halakat fogyasztanak. Szaporodása márciustól június végéig is elhúzódhat. Íváskor a növényekkel benőtt szélvizekbe, folyóknál az elöntött hullámtérre vonul. A növényzetre tapadó megtermékenyített ikrát nem őrzik. Megnyúlt, aránylag alacsony, de a süllőnél magasabb hátú, oldalról lapított testű ragadozóhal. Feje hosszú, csúcsba nyíló szája és a szeme nagy. Szájában erős fogak ülnek, de a többi közül kiemelkedő ebfogai csak a 10-15 centiméternél kisebb, fiatal példányoknak vannak."),
            new ClassHal("Laposkeszeg", "Ballerus ballerus", R.drawable.laposkeszeg, "Őshonos, fogható faj", null, 0, 0, " A nagyobb folyók jellemző keszegfaja, de az állandó vízáramlás nélküli állóvizekben is előfordul. A kifejlett egyedek jellemző testhossza 20-30 centiméter, de a 45 centimétert is elérheti. A dévérkeszegnél kevésbé magas hátú, de rendkívül lapított testű hal. Feje kicsi, orra rövid és hegyes. Kis szája félig fölső állású, pikkelyei aprók. Farokalatti úszója feltűnően hosszú. Farokúszója mélyen bemetszett, alsó lebenye kissé hosszabb a felsőnél. Elsősorban planktonnal táplálkozik, és a vízre hulló rovarok mellett sok szerves törmeléket is fogyaszt, fenéklakó állatot viszont csak keveset, ennek megfelelően főként vízközt és a felszín közelében mozog. Április és május között ívik, az ikrákat legszívesebben az elöntött szárazföldi növényzetre rakja."),
            new ClassHal("Leánykoncér", "Rutilus virgo", R.drawable.leanykoncer, "Védett faj", null, 0, 0, "Folyóvízi hal. A leánykoncér a nagy folyókban (főleg a Dunában, Drávában) jellemző. Leginkább a bodorkával keverhető össze, de annak a szája csúcsba nyíló, szeme nagyobb, szemgyűrűje rendszerint narancsos árnyalatú, míg a leánykoncérnak félig alsó állású a szája, a fejéhez képest kisebb a szeme, szemgyűrűje pedig fehéres. A jászkeszeg szája csúcsba nyíló, pikkelyei apróbbak, számuk az oldalvonalon 55-60, míg a leánykoncér jóval nagyobb pikkelyeket visel: az enyhén ívelt oldalvonala mentén 44-49 pikkely számolható."),
            new ClassHal("Lápi póc", "Umbra krameri", R.drawable.lapi_poc, "Védett faj", null, 0, 0, "Az apró lápi póccal kisebb, mocsaras állóvizekben vagy csatornákban találkozhatunk. Jellegzetes, zömök testalkata és barnás, finom fekete mintázatú színe miatt kevés fajjal lehet összetéveszteni, egyedül a hasonló életteret kedvelő amurgéb emlékeztet jobban rá, de az amurgébnek két hátúszója van, míg a lápi pócnak csak egy."),
            new ClassHal("Lénai tok", "Acipenser baerii", R.drawable.lenai_tok, "Idegenhonos faj", null, 0, 0, "Más nevén szibériai tok. Természetes élőhelyein veszélyeztetett, de keresztezési és tenyésztési célból más országokban is előfordul és ott természetes vizekbe is kikerült. Magyarországon is megtalálható a faj, hiszen különlegessége miatt több intenzív telepítésű tóba betelepítették. Természetes állománya csökkenőben van, ezért a szibériai tok őshazájában védettség alatt ál. Magyarországon megtalálható több intenzív telepítésű tóban és előfordul a Drávában, az Orfűi-tóban és a Bikazugi Holt-Körösben. Nagy gazdasági jelentőséggel bír, hogy \"a kaviárként\" emlegetett fekete kaviárt a hal ikrájából, továbbá a sőregtok és a viza ikráiból lehet készíteni. Egy átlagos kiszerelésű 113,6 grammos beluga kaviár 80 000 forint körüli összegbe kerül. A fajnak három formáját írták le az élőhelyük és a szaporodásuk érdekében történő vándorlásuk szerint. Az Ob és Jenyiszej folyókban élő vándorló formája az életének jelentős részét tengeröblökben és folyótorkolatokban tölti, viszont szaporodáskor akár 2-3 ezer kilométert is felúszik ezeken a folyókon az ívó helyére. A Léna, a Jana, az Ingyigirka és a Kolima folyókban él a folyóvízi formája, mely egész életét a folyókban tölti és viszonylagosan rövid utat tesz meg ívó helyeinek elérése érdekében. Ez a forma adta a lénai tok elnevezést, mely 1981-ben került Magyarországra.A legjobban hasonlít a Magyarországon is megtalálható tokfélékhez. A többi tokfajtól azonban bajuszszálainak hossza és szájnyílásának alakja különbözteti meg. A viza bajuszszálai is elérik a felső ajkat, de nagy szája szinte a fej egyik szélétől a másikig ér. A sima tok ajkai megszakítás nélküliek. A vágó tok és a sőregtok bajuszszálai nem érik el a felső ajkat. A kecsege bajuszszálai szintén elérik a felső ajkat, de rojtozottak. Élőhelyén nagyra növő fajta, hiszen testhossza elérheti a 3 métert, tömege a 100 kilogrammot. Teste hengeres alakú, feje kúpos, orra megnyúlt. Testszíne a háton barnásfekete vagy barnásszürke, halványabb árnyalatban hasonló az oldala is, a hasa fehéres színezetű. Alsó állású szája közepes méretű. Alsó ajka középen megszakított, felső ajka – szintén a középtájon – az alsó ajak felé enyhén becsúcsosodik. Hosszú és sima bajuszszálai közelebb erednek az orrcsúcshoz, mint a szájnyíláshoz, s hátrasimítva a felső ajkat elérik és a többi tokfajtól ez különbözteti meg. A farokrészen elhelyezkedő hátúszójában 30-56, farkalatti úszójában 17-33 úszósugár található. Vértjei beolvadnak a test színébe, attól élesen nem különülnek el. Vértpikkelyeinek száma a háton 10-20, az oldalán 32-62, a hason 7-16 (20). Étrendjét főként fenéklakó gerinctelen szervezetek alkotják, de néha kisebb halakat is elfogyasztanak. Életmódja miatt a leggyakoribb horgászmódszer a fenekező készséggel történő horgászata. Étrendje miatt csaliként szóba jöhetnek a földigiliszta-félék, a csontkukac, a piócák esetleg nagyobb példányait kishallal lehet zsákmányul ejteni. A szájba akadt tokok fárasztása, partra emelése viszonylag problémamentes, 5-15 perces fárasztást igényel. A partra emelés után ajánlatos egy nagyobb méretű pontymatrac használata, hiszen a méretes példányok elég sérülékenyek és ez különösen visszaengedésekor ajánlott."),
            new ClassHal("Magyar bucó", "Zingel zingel", R.drawable.magyar_buco, "Védett faj", null, 0, 0, "A magyar bucó Magyarországon fokozottan védett hal, természetvédelmi értéke 100 000 Ft. A faj elterjedése szűk területre a Duna és a Dnyeszter vízgyűjtő területére korlátozódik. Magyarország vizeiben nemcsak őshonos, hanem a faj kialakulása és elterjedése is a Duna-medencében ment végbe. Magyarországi élőhelyeként ismert a Mosoni-Duna, a Rába, a Lapincs, a Pinka, a Strém, a Marcal, az Ipoly, a Dráva, a Mura, a Tisza, a Szamos, a Keleti-főcsatorna, a Hármas-Körös, Kettős-Körös, Sebes-Körös, Fekete-Körös, Fehér-Körös és a Maros folyó. Közeli rokonához a német bucóhoz, hasonlít a legjobban, olyannyira, hogy ránézésre összetéveszthető vele. Megkülönböztetést segíti, hogy annak a teste és faroknyele valamivel vékonyabb, mintázata markánsabb. Tőle eltérően oldalát rendszerint igen határozott szélű ferde sávok mintázzák, faroknyele még jobban elvékonyodik, és első hátúszójában csak 8-9 tüske található, valamint tőle nagyobbra is nő. A magyar bucó első hátúszója hosszabb és benne több 16 feletti tüske van. Alakját tekintve hasonlít még a küllőkre és a márnákra, de a bucófajokat első pillantásra megkülönbözteti kettős hátúszójuk. Általában 20–30 cm és nagyon ritkán 50 centiméter hosszúságot is elérő fenéklakó hal. Orsószerűen megnyúlt a teste, melynek keresztmetszete csaknem henger alakú és a farok felé folyamatosan vékonyodik. Keskeny faroknyele rövidebb, mint a második hátúszó tövi részén mért távolság. Felülről lapított hosszúkás a feje, mely háromszög alakot formáz. Szeme nagy, egymástól függetlenül tudja mozgatni és orrához viszonyítva átmérője fele olyan nagyságú. Szája testéhez viszonyítva nem nagy és a nyílása alsó állású. Közepes méretű, kopoltyúfedő hátsó peremén erős tüske található, az elő kopoltyúfedő hátsó része fogazott. Pikkelyei apróak, érdesek és erősen tapadnak, 83-95 darab van az oldalvonalán egy sorban. Két hátúszója távol helyezkedik el egymástól, az elsőben 13-15 tüskés, a másodikban 16-20 lágy elágazó sugár van. Nincs úszóhólyagja. Háta és oldalai sárgásbarnák, benne 6-7 elmosódott sötét sávval vagy folttal tarkított rész színesíti. A hasának színe ezektől eltérően fehéres. A folyóvizekben az erősebb és gyengébb áramlásban fordul elő, mivel az áramlásmentes területekről elhúzódik. Magas oxigénigényű reofil halfaj, példányai a folyók medrében, a parttól viszonylag távol keresik táplálékukat. A vízszennyezésekre kevésbé érzékeny mégis jelentősen lecsökkent az állománya. A folyókon megépülő vízlépcsők lassú áramlási viszonyokat hoznak létre és emiatt veszélyeztetik az állományát. A duzzasztók felső szakaszán nem találja meg életfeltételeit és szaporodásához szükséges iszapmentes aljzatot is elveszíti. Életmódjához igényli a nagy vízteret, ezért a kisebb folyóvizekben nem tud megélni. Tavakba kerülése esetén elvándorol vagy elpusztul. Mozgása szakaszos, hirtelen kifutásokból és pihenő szakaszokból áll. Táplálékát a görgetett hordalékból szedegeti össze és étrendjét az apróbb fenéklakó szervezetek, puhatestűek, férgek, lárvák esetleg szerves törmelék, halikra és halivadék jelenti."),
            new ClassHal("Menyhal", "Lota lota", R.drawable.menyhal, "Őshonos, fogható faj", null, 25, 0, "Egyetlen tőkehalfélék családjába tartozó halfajunk. Az élénkebb sodrású hideg vizet és a kemény aljzatot kedveli, ezért nagyobb folyókban érzi igazán jól magát. Éjszakai életmódú, fenéklakóhal, többnyire a kövek között vagy a part üregeiben búvik meg. A nagyobb példányok hossza 40-50 cm, ritkán 60 cm körüli. Nyúlánk, a feji részén fölülről, farki részén oldalról lapított hal. Feje aránylag rövid, de széles, orra közepesen hosszú, a szeme kicsi. Szája nagy, félig alsó állású, benne apró, hegyes fogak ülnek. Szájhasítéka vízszintes, oldalról nézve a szem alá, többnyire annak hátsó vonaláig ér, alsó állkapcsáról egyetlen bajuszszál csüng. Az első hátúszó nagyon kicsi, a második viszont a lekerekített farokúszóig ér. Farokalatti úszója nagyon hosszú. Különálló farokúszója – ami alapján könnyen, első látásra elkülöníthető a harcsától – kicsi és szimmetrikusan lekerekített. Hasúszói szokatlanul elöl, a toroktájon helyezkednek el. Pikkelyei nagyon aprók, alig észrevehetők. Oldalvonala a test elején folytonos, a farokúszó közelében szakadozott. Testének színe zöldesbarna sötétebb márványozással. A fiatalabb egyedek táplálékát elsősorban fenéklakó gerinctelen állatok alkotják, a 20 cm fölöttiek viszont áttérnek a ragadozó életmódra, egyre inkább halakat fogyasztanak. Alakja, színezete a harcsához teszi hasonlatossá, de annak csupán egyetlen, igen rövid hátúszója van, bajusza ellenben 6, köztük 2 igen hosszú is. Szaporodása a téli időszakban több részletben történik, jellemzően a decembertől februárig tartó időszakra tehető. Ikráit a mélyebb részekre rakja le, ahol kezdetben lebeg, majd később a kövekhez tapad."),
            new ClassHal("Márna", "Barbus barbus", R.drawable.marna, "Őshonos, fogható faj", "04.15.-05.31.", 40, 0, "Sebes folyású, oxigénben gazdag, mély vizeket kedveli, tipikus folyóvízi hal. A duzzasztott szakaszokat kerüli. A kifejlett példányok 40-50 cm hosszúak, de 70 centiméternél hosszabbra is megnőhet. Teste izmos, nyúlánk és hengeres, oldalról enyhén lapított. Hosszú orra túlér a száján. Húsos szája alsó állású, melynek szögletében 2 pár bajuszszál található. Hátúszójának első sugara hátulsó oldalán fogazott. A farokalatti úszó nem hosszú, hátrasimítva csúcsa meg sem közelíti a farokúszó tövét. Háta zöldesbarna, zöldes-fehéres testén apró pikkelyek vannak. A hasa sárgásfehér, úszói vöröses színűek. Táplálkozása változatos, étrendjébe a tartozik a víz fenekén élő lárva, csiga, féreg, amelyek után állandóan turkál. Elfogyasztja a növényi eredetű táplálékot és a fiatal halivadékot is. Szívesen tartózkodik a szennyvízbefolyások utáni vizeken, hídlábak mögött, patakok, folyók egyesülése utáni szakaszokon. Húsos ajkai segítségével leharapdálja a kövek algaszőnyegét. Ívása május-június hónapban, a folyók kavicsos-sóderes mederszakaszain zajlik."),
            new ClassHal("Naphal", "Leopomis gibbosus", R.drawable.naphal, "Idegenhonos, invazív faj", null, 0, 0, "Eredeti hazája Észak-Amerika, Magyarországra az akvaristák közvetítésével jutott el. Akváriumi halként kedvelik, mert szép küllemű, szívós, és nem utolsó sorban könnyen tartható hal. Mint a neve is utal rá, a naphal szereti a napfényt, így legnagyobb állományai azokban a sekély, part menti állóvizekben alakulnak ki, ahol már van hínárnövényzet, de még nem uralkodott el teljesen. Kedveli a gyorsan melegedő szélvizeket. A mély holtmedreknek csak a parti részeit lakja. A fiatalabb bányatavakban gyakran nagyon sűrű népessége él. Kisebb-nagyobb folyók lassú áramlású részin előfordul, de nem gyakori. Kistermetű hal, a nagyobb példányok testhossza is csak 10-15 cm körüli. Teste zömök, igen magas, oldalról erősen lapított. Feje viszonylag nagy, orra rövid, alig hosszabb, mint a szem átmérője. A sügérfélékhez hasonlóan kettős hátúszója összenőtt, nem két külön egységből áll. A kifejlett példányok kopoltyúfedőjének felső részén egy fülszerű, fekete-piros folttal díszített bőrfüggelék van. Szája kicsi és felső állású, hasítéka is fölfelé irányul. Hasúszói előre tolódtak, alig valamivel a mellúszók mögött kezdődnek. Pikkelyei aránylag nagyok, erősen ülnek. Színe kékes olajzöld, tarka mintázattal. Szaporodása május-június, de kedvező körülmények között július-augusztusban másodszor is szaporodhat. az ívásra készülő hímek a sekély szélvizek gyökerekkel átszőtt aljzatán lapos mélyedést készítenek. Az odacsalt nőstény ebbe rakja le ikráját, amit megtermékenyítés után a hím őriz, miközben a nőstényt elűzi a közelből. Főként a parti sáv gerinctelen állataival, rovarlárvákkal táplálkozik. Abban a víztestben, ahol előfordul a naphal, a süllő és a csuka, ott ezek kedvelt táplálékai közé tartozik."),
            new ClassHal("Nyúldomolykó", "Leuciscus leuciscus", R.drawable.nyuldomolyko, "Védett faj", null, 0, 0, "A kicsi és félig alsó állású szájjal és homorú farokalatti úszóval rendelkező nyúldomolykó hasonlít a domolykóra, de annak feje és teste jóval vaskosabb, csúcsba nyíló szájának szöglete elér a szem vonaláig, és farokalatti úszójának a széle domborúan ívelt. Mivel a nyúldomolykó kistermetű hal, a domolykó méretkorlátozását betartva még véletlenül sem fog domolykónak vélt nyúldomolykót megtartani a horgász."),
            new ClassHal("Német bucó", "Zingel streber", R.drawable.nemet_buco, "Védett faj", null, 0, 0, "Kisméretű halfaj, mely gazdasági szempontból nem bír jelentőséggel, de nagy természeti értéket képvisel. Szűk területen élő, nagyon ritka bennszülött halunk. A veszélyeztetett fajok közé tartozik, mivel a folyókon épülő vízlépcsők hatására élettere egyre szűkül. A német bucó Magyarországon ritkán előforduló fokozottan védett hal, természetvédelmi értéke 100 000 Ft. Elterjedési területe nagyon szűk a Duna vízrendszerének őshonos halfaja a tiszta, oxigéndús, sebes folyóvizekben él. A Duna vízrendszerén kívül a Vardar folyó vízrendszere szolgál még élőhelyeként. Magyarországi élőhelyei a Duna, Mosoni-Duna, a Rába, a Lapincs, a Pinka, a Strém, az Ipoly, a Dráva, a Mura, a Tisza, a Szamos, a Sajó, a Bódva, a Hernád, a Vadász-patak, a Sebes-Körös és a Maros. A Duna vízrendszerén kívül, a Dnyeszter vízrendszerében is előfordul. Közeli rokonához a magyar bucóhoz, hasonlít a legjobban, olyannyira, hogy ránézésre összetéveszthető vele. Megkülönböztetésül annak a teste és faroknyele valamivel vaskosabb, mintázata elmosódottabb. A magyar bucó első hátúszója hosszabb és benne több 16 feletti tüske van. Alakját tekintve hasonlít a küllőkre és a márnákra, de a bucófajokat első pillantásra megkülönbözteti kettős hátúszójuk. Kistermetű faj a hal teste hengeres erősen megnyúlt, hossza 12 - 18 centiméter, legfeljebb 22 centiméter. Feje viszonylag hosszú és aránylag széles, valamint felülről lapított. Orra megnyúlt, körülbelül kétszerese a szem átmérőjének. Közepes méretű szája teljesen alsó állású. Érdes tapintású kicsiny pikkelyei erősen ülnek a bőrben darabszáma 70 - 82 egy hosszanti sorban. A torok, a mell és a has nagy része pikkelytelen. A kopoltyúfedők hátulsó szegélyén erős tüske ül. Mellúszói gyengén fejlettek, a hasúszók a mellúszók alatt vannak, szélesek. Az első hátúszóban 7-9 tüske, a másodikban 12-13 elágazó sugár számlálható. Az elő-kopoltyúfedők hátulsó szegélye fogazott. Nincs úszóhólyagja. Oldala ívás idején smaragdzöld bronzfénnyel ragyog. A német bucó fenékhal, a bucó halnem más fajaihoz hasonlóan a kavicsos talajú, sekély, de erős sodrású vizeket vízfolyások felső és középső szakaszán a gázlós, gyors sodrású köves, kavicsos biotópokat kedveli. Az éjszaka aktív hal a nagyobb folyókban a paduczóna fölső régiójától a dévér zóna fölső régiójáig fordul elő, ez utóbbinak az alsó régiójában - ellentétben a magyar bucóval - már nem található meg. Általában a sodrott mederszakaszokon található meg, az üledékes részeket kerüli, ezért a duzzasztott folyószakaszokról elvándorol. Tápláléka apró talajállatok, halikra, ivadék, de a víz által görgetett hordalékból is kiszedegeti az elfogyasztható szerves törmeléket."),
            new ClassHal("Paduc", "Chondrostoma nasus", R.drawable.paduc, "Őshonos, fogható faj", "04.15.-05.31.", 20, 0, "Kifejezetten folyóvízi hal, a gyorsan áramló, oxigénben dús vizet kedveli. Folyóink egyik tipikus keszegfaja. Nagyobb példányai 25-35 cm-esek, kivételesen 40 centi fölöttiek is lehetnek. Teste megnyúlt, oldalról lapított, áramvonalas, a folyó sodrásához teljes mértékben idomult. Feje aránylag kicsi, hosszú orra a száj elé nyúlik. Alsó állású szája alulról nézve csupán enyhén ívelt, majdnem egyenes vonalú. Alsó ajka a rajta képződő szarubevonattól kemény és éles. A farokúszó mélyen bemetszett, mellúszói viszonylag nagyok. Farokalatti úszójában általában 10-12 elágazó sugár van (tehát jóval kevesebb, mint a szilvaorrú keszegnél). Teste a háti részen feketészöld, az oldalain ezüstösen csillogó, amit a szürkés-vöröses farokúszó és a vöröses színű úszók díszítenek. Színei ívási időben különösen élénkké válnak. Táplálékát elsősorban a mederfelszínen kialakuló élőbevonat alkotja, amely főleg fonalas algákból áll. Sekélyebb részeken oldalra fordulva, vésőszerűen éles, szarubevonatos ajkával szedegeti a kövekre élőbevonatot. Április-májusban ívik a folyók erősebb áramlású szakaszain, ikráit kavicsos aljzatra rakja."),
            new ClassHal("Pettyes busa", "Hypophthalmichthys nobilis", R.drawable.pettyes_busa, "Idegenhonos, invazív faj", null, 0, 0, "A pettyes busa Dél-Kína meleg vizű mély folyóiban és tavaiban őshonos. Közép- és Kelet-Európában betelepítették (a Duna vidéke, a Fekete-, Azovi- és Kaszpi-tengerek folyói). Magyarországra a fehér busa-ivadékokkal került, tógazdaságokban nevelik. Európai állományai növekednek.  hal teste nyújtott, oldalról lapított, háta kissé magas, feje széles, csúcsos. Szája felső állású, bajuszszálak nélkül, szájrése rézsút felfelé irányul. Szemei kicsinyek, nagyon mélyen ülnek (sokkal mélyebben, mint a fehér busánál). Hasoldalán, a hasúszók és a farok alatti úszó között él húzódik. Pikkelyei kicsinyek, 114-120 az oldalvonal mentén. Hátúszója 10, farok alatti úszója 15-17 sugarú, az első 3 gyengén elcsontosodott. Garatfogai egysorosak, 4-4. Átlagos testhossza 55-70 centiméter, de akár 146 centiméteresre és 40 kilogrammosra is megnőhet. A fehér busával szemben nemcsak kezdetben, hanem később is állati planktonnal él. A kifejlett példányok bélcsatornája csak a testhossz 4-5-szörösére nő. 19 Celsius-fok vízhőmérséklet alatt a kifejlett egyedek főként állati planktont és fenéklakó apró állatokat esznek, a növényi planktonra csak akkor térnek át, ha a hőmérséklet emelkedik. Az íváshoz tartósan meleg (25 Celsius-foktól) vizet igényelnek. Gyakran úszik 5-7 egyedet számláló csoportokban a vízfelszín közelében. Ha megriad, akár 3 méteres magasságba is képes kiugrani a vízből. Legfeljebb 20 évig él. A vadonban vándorutakat tesz meg ívási célokból. A mély, zavaros, de oxigéndús helyekre rakja le ikráit. Egyszerre akár 100 ezer ikrát is rakhat. Az ikra átlátszó, azonban sárgás árnyalatú. 25 Celsius-fokos vizekben, akár két nap alatt is kikelhet az ivadék, mely a folyómeder fenekén rejtőzködik. iután a volt Szovjetunióban eredményesen keresztezték a fehér busával (Hypophthalmichthys molitrix), és az ivadékot exportálták, a hazai vizekben is gyakoriak a hibridek. A pettyes busának ipari mértékű halászata van. Több helyen tenyésztik is. Városi akváriumokban is látható. Sok helyen ahová betelepítették, károsnak bizonyult."),
            new ClassHal("Petényi-márna", "Barbus peloponnesius", R.drawable.petenyi_marna, "Védett faj", null, 0, 0, "A márnához nagyon hasonlító Petényi-márna szintén kis növésű hal, a márna méretkorlátozását betartva véletlenül sem fog márnának vélt Petényi-márnát megtartani a horgász. Igen jó fajbélyeg a hátúszó első úszósugara, ami a márnánál kemény, fogazott csonttüske, ilyet nem találunk a Petényi-márnánál. Emellett a farokalatti úszó hátrasimítva a márnánál meg sem közelíti a farokúszó tövét, míg a Petényi-márna farokalatti úszója hátrasimítva megközelíti vagy el is éri azt. A bihari márna endemikus, azaz bennszülött halfaj. Nevét arról a tájegységről kapta, amelyet a Sebes-Körös átszel. A folyó hosszabb szakasza Románia területén folyik, így a bihari márna populációjának nagyobbik hányada is a határon túl él."),
            new ClassHal("Pisztrángsügér", "Micropterus salmoides", R.drawable.pisztrangsuger, "Idegenhonos faj", null, 0, 0, "Észak-Amerika keleti felén Kanadától Mexikóig őshonos, de Amerikán kívül sok helyre betelepítették. Magyarországra 1909-ben hozták be, de az elterjesztésére tett kísérletek nem jártak igazi sikerrel, stabil állománya kevés helyen alakult ki. Főként a sekély és mély vízzel is rendelkező kemény medrű tavak és a nem túl gyors, de kemény aljzatú folyószakaszok biztosítanak számára megfelelő élőhelyet. A nagyobb példányok 30-40, olykor 50 cm körüliek. Megnyúlt, közepesen magas, oldalról lapított testű hal. Feje nagy és vaskos, az orra mérsékelten hosszú, de jóval fölülmúlja aránylag kicsi szemének átmérőjét. Nagy szája fölső állású, hasítéka ferdén fölfelé irányul. Hátúszójának első része sokkal alacsonyabb, mint a hátulsó. Farokúszója jól fejlett, széle kissé beöblösödő. Hasúszói a mellúszók alatt foglalnak helyet, azokkal szinte egy vonalban kezdődnek. Pikkelyei kicsik, de erősen ülnek. Színezete barnászöld, a fiatalokon hosszanti fekete mintázattal, amely az idősebbeken elhalványul, majd eltűnik. Kezdetben planktonnal, később rovarlárvákkal és egyéb gerinctelen állatokkal táplálkoznak, majd egyre több halat is fogyasztanak. Szaporodása májusra tehető, íváskor a tejesek a kavicsos-homokos aljzaton tányérszerű gödröt mélyítenek, ebbe kerülnek az ikrák. A megtermékenyített ikra a hímek védelme alatt fejlődik, de érzékenysége miatt a kelési arány gyenge."),
            new ClassHal("Ponty", "Cyprinus carpio", R.drawable.ponty, "Őshonos, fogható faj", "05.02.-06.15.", 30, 0, "Az egyik legismertebb és legnépszerűbb halunk a ponty, amelynek színe a zöldesszürkétől a narancssárgáig, formája a tányérszerűen kerektől a mérsékelten magas hátú tőpontyon át a megnyúlt testű nyurgapontyig változhat. Hazai állományának túlnyomó többségét mesterséges szaporítással előállított telepített pontyok adják. A nagyobb egyedek hossza 40-50 cm, de 1 méternél nagyobbra és akár 30 kg-osnál nagyobbra is megnőhet. A bőre alapján elkülönítjük a pikkelyes és a csupán egy-két sor pikkelyt viselő tükörpontyot. Szereti a náddal szegélyezett, jól felmelegedő, iszapos aljzatú sekélyebb tavakat és a lassan folyó vizeket. Szája körül 2 pár bajuszszálat visel, az egyiket a felső ajakon, a másikat a szájszegletben. Legjobban az ezüstkárász hasonlít hozzá, de annak nincs bajusza, továbbá összetéveszthető a nem fogható széles kárásszal is, de annak sincs bajusza, és hátúszója lekerekített, domború szélű. Az életmódjára az állandóan vándorló, folyamatos táplálékkereső életmód a legjellemzőbb. Főként iszaplakó gerinctelen állatokkal, (vízi rovarokkal, rovarlárvákkal, csigákkal, piócákkal) táplálkozik, valamint a vízi növények fiatal hajtásaival, de más növényi eredetű táplálékot is fogyaszt. Szaporodási ideje jellemzően májusra esik, de április közepétől akár június végéig is tarthat. Kisebb csoportokban ívik, ikráit a vizek növényzetben gazdag sekély részeire rakja le."),
            new ClassHal("Pénzes pér", "Thymallus thymallus", R.drawable.penzes_per, "Védett faj", null, 0, 0, "Európa hegyi folyóinak lakója ez a horgászok által kiváló sporthalként számon tartott faj. Horgászata a pisztrángozáshoz hasonlóan nagy élményt nyújt. Húsa szálkamentes és fehér húsa vetekszik a pisztrángéval. A magyarországi halfaunának nem állandó tagja, de mint természeti érték, minden alkalmilag felbukkanó példány védelmet élvez. A pénzes pér más elnevezése még a pénzes pisztráng, a lepényhal, és az ón. A német sporthorgász szövetség és az osztrák testvérszervezet kuratóriuma 2011-re kiválasztotta, mint az év halát, hogy ezzel is ráirányítsa a fajra a természetvédők figyelmét. A pénzes pér Nyugat-, Közép- és Kelet-Európa kemény talajú, gyors folyású, oxigéndús vizeiben él, de elterjedése nagyon rendszertelen. Állományai többfelé nagyon megfogytak és Magyarország vizeibe csak alkalmilag téved. Különösen érzékenyen reagál a környezetszennyezésre, ezért azon fajok közé tartozik, melyek az elsők között tűnnek el a vízminőség romlása esetén. A pontyféléktől jól megkülönböztethető azáltal, hogy a pér zsírúszót is visel. A hal teste mérsékelten nyújtott, oldalról lapított, feje kicsi, szeme nagy és az orra hegyes. Cikloid pikkelyei kicsinyek, 74-96 darab található az oldalvonal mentén. A szokatlanul magas, hosszú hátúszó és a farokúszó között zsírúszó van. Szájnyílása kicsi, legfeljebb a szem elülső szegélyéig ér; felső állkapcsa előrenyúlik. Mindkét állkapcsán ülnek fogak. A hátúszó jóval a hasúszók eleje előtt kezdődik; a hím hát- és farok alatti úszója, valamint hasúszói hosszabbak a nőstényénél. Hátoldala szürkészöld vagy kékesszürke, oldalai és hasoldala az ezüstfehértől a sárgaréz színűig változik, ívás idején bíboros csillogással. Hátán és oldalain 3-5 sorban elszórt sötétebb zöldes-lilás foltok vannak. Szürke színű hátúszója 4-5 sorban elhelyezkedő vörhenyes szemfoltokkal. A páros úszói sárgásak, pirosas árnyalattal. Ívási időszakban a hímek oldala megváltozik és vörhenyes árnyalatúra vált. Testhossza általában 25-35 centiméter, de elérheti a 60 centiméteres hosszúságot és a 6,7 kilogrammos testtömeget is. A hegyi patakok bővebb vizű szakaszain fordul elő. Gyakran felhúzódik a pisztráng életterébe, de nagyobb áradások után az alacsonyabb, paduc vagy márna szinttájon is megtalálható. Kivételesen tavakban is kialakulhat állománya, de ívni a befolyó patakokba vándorol. Tápláléka férgek, csigák, vízi és repülő rovarok, apró halak és ikrák. A pénzes pér, március és június között ívik 6-8 °C-os hőmérsékletű vízben. A hímek a második, a nőstények a harmadik éves korukban lesznek ivarérettek. Az ikrás az elöntött kavicszátonyon gödröt kapar és a 2,5-4 milliméter átmérőjű ikrákat a megtermékenyítés után kaviccsal takarja be. Az ikrák száma 3000-6000 között van. A 8-9 milliméter hosszú lárvák 20-30 nap múlva bújnak elő."),
            new ClassHal("Razbóra", "Rasbora", R.drawable.razbora, "Idegenhonos, invazív faj", null, 0, 0, "Eredeti hazája az Amur folyótól délre eső terület, Európába véletlenül került az amur és a busafajok ivadékaival. Hazánkban 1963-ban észlelték először. Jelenleg a Kárpát-medencében és a környező területeken él, de terjed. A könnyen felmelegedő nyugodt vizeket kedveli, legnagyobb állományaival a sekély tavakban és az alig mozgó vizű kisebb csatornákban találkozhatunk, de a hegyi patakok kivételével szinte minden típusú vízben fellelhető. Apró termetű faj, a nagyobbak hossza 8-10, legfeljebb 12 cm lehet. Teste megnyúlt, oldalról lapított. A fiatalok nyúlánkabbak, az idősebbek vaskosabbak és zömökebbek, hátuk és faroknyelük magasabb. Hátvonalának íve a hátúszó kezdetéig domború, onnan kezdve enyhén homorú. Feje alacsony és kúpos, orra rövid, a hossza alig nagyobb a szem átmérőjénél. Fölső állású szája kicsi, hasítéka meredeken fölfelé irányul. Hátúszójának és farokalatti úszójának széle domborúan ívelt. Pikkelyeinek hátsó széle sötétebb árnyalatú, ami olyan hatást kelt, mintha a bőrből kidomborodnának. A fiatalok és az ikrások oldalán egy sötét csík húzódik végig az orrtól a szemen át egészen a farokúszóig. Kezdetben csak igen apró planktonszervezeteket fogyaszt, később nagyobb planktonrákokkal, rovarlárvákkal, valamint a víz alatti tárgyakon és növényeken kialakuló élőbevonattal táplálkozik. Március közepétől június végéig ívnak több részletben valamilyen szilárd aljzatra. Az ikrát a hím kikelésig őrzi, gondozza."),
            new ClassHal("Réti csík", "Misgurnus fossilis", R.drawable.reticsik, "Védett faj", null, 0, 0, "A nagy folyószabályozások és a mocsarak lecsapolása miatt megfogyatkozott a Magyarországon található állománya, ezért védelmet élvez. Korábban tömegesen fordult elő, fontos böjti tápláléknak számított, és a csíkászatot a 19. századig mint a halászat külön ágazatát tartották számon. Nevezik még réticsíknak, csíknak és halcsíknak. A színváltozatai miatt megkülönböztetésképpen használt elnevezései a barnacsík, a csíkdáma és a csíkkirály. Európa és Ázsia különböző részein honos. Európa északi és keleti részében szinte mindenütt előfordul, megtalálható az Alpoktól északra, egészen Maasig kelet felé a Néva vízrendszerében a Ladoga-tó, a Fekete-tenger északi medencéjének folyóiban a Dunában kelet felé a Kubánban, a Kaszpi-tó folyóiban, a Volga és az Ural vízgyűjtő rendszerében elsősorban az iszapos fenekű tavak és folyók lakójaként. Nem őshonos az Egyesült Királyság, Skandinávia és az Appenninek folyóiban az Ibériai-félszigeten, a Krímben, az Adriai-tenger és az Égei-tenger vízgyűjtő rendszerében. Hasonló fajok a csíkfélék tagjai és felületes szemlélő összetévesztheti még az fiatal angolnával, de az nyúlánkabb és a hasúszói hiányoznak. A közeli rokon kövi csík oldalát foltok tarkítják, míg a réti csíkon hosszanti csíkok láthatók ezért jól megkülönböztethetőek egymástól. A vágó csík és a kőfúró csík (törpe csík) feje és teste lapított és a szemük hátrafelé áll, valamint felmereszthető a csonttüskéjük. Nagyságát tekintve 15-35 centiméteresre nő meg és tömege 80-150 gramm között mozog. Kisméretű száját 10 bajusz köríti, 4 a felső és 6 az alsó ajakán. A testformája miatt kígyószerűnek tűnő halat a fenéken bajuszszálai segítik az érzékelésben. Teste hengeres, nagyon izmos, nyákos és apró pikkelyek borítják, melyek alig fedik egymást. A szemei testméretéhez viszonyítva nagyon aprónak tűnnek. Hasa sárga, háta barna és oldalán a szemétől a farokúszóig jellegzetes sötétbarna csíkok húzódnak. Éjjel aktív hal, napközben az iszapban rejtőzik el. Étrendjét tekintve rovarokat, férgeket és ízeltlábúakat keresgél az iszapban, valamint a vízben lévő növényi törmelékeket is elfogyasztja. Nagyon szívós és igénytelen hal, a telet az iszapba rejtőzve tölti el. A víz időleges kiszáradását is el tudja viselni. Élőhelye az árterek, mocsarak, lápok iszapos fenekű helyei, de megtalálható a folyóvizek lassú folyású, iszapos szakaszain is. Élőhelyspecifikus faj, ami azt jelenti, hogy egy bizonyos élőhelytípushoz alkalmazkodott, és ettől a környezettől nem tud elszakadni. Az élőhelyének változásakor nem tudja követni a változásokat és emiatt eltűnik a faj az adott területről. Szürkületkor kezd aktív lenni és a faj jellegzetessége, hogy kisegítő légzőszerve segítségével béllégzésre képes. Amikor kevés a vízben az oxigén felúszik a felszínre, és ott levegőt szippant, amelyet vérerekkel dúsan átszőtt belébe présel. Az elhasznált levegőt a végbelén át engedi ki. Zivatar előtt gyakran használ légköri oxigént, mivel ilyenkor az emelkedő hőmérséklet és a süllyedő légnyomás miatt csökken a víz oxigéntartalma. Régebben ezen tulajdonsága miatt a csíkászok élő barométernek tartották, mert az időjárás változását nyugtalan mozgásukkal előre tudták jelezni."),
            new ClassHal("Sebes pisztráng", "Salmo trutta", R.drawable.sebes_pisztrang, "Őshonos, fogható faj", "10.01.-03.31.", 22, 0, "Egy eredetileg tengerben élő pisztrángfajból alakult ki, amely csak szaporodás idején kereste fel az édesvizeket. A nálunk élő sebes pisztráng teljesen áttért az édesvízi életmódra, a bővizű hegyi patakokban és folyókban uralkodó viszonyokhoz alkalmazkodott. A sebes sodrású, oxigénben dús és hideg vizeket kedveli. Alkalmas élőhelyen több kilós testtömeget is elérhet, de természetes vizeinkben ritkán akadnak 30 centinél nagyobb példányok. Teste orsószerű, oldalról kevéssé lapított. Feje nagy, orra tompa és rövid. Szája nagy és csúcsba nyíló, fogai erős ragadozófogak. Fölső állkapcsa eléri a szem hátsó szélének vonalát vagy túlnyúlik azon. Hát- és farokúszója között kicsiny zsírúszót visel, farokúszójának széle kevéssé beöblösödő. Hátát, oldalát, esetleg úszóit világos udvarral körülvett fekete és rendszerint piros pettyek is díszítik. A fiatalok testoldalán az ezüstös alapszínnél kissé sötétebb, nagy foltok is láthatók. Főleg bolharákok, rovarlárvák és vízre hulló rovarok alkotják a táplálékát, de a nagyobb példányok halakat is zsákmányolnak. Szaporodása októbertől decemberig tart, az ikrát a patakok medrében készített mélyedésbe rakják, amelyet a megtermékenyítés után az ikrás kaviccsal takar be."),
            new ClassHal("Selymes durbincs", "Gymnocephalus schraetser", R.drawable.selymes_durbincs, "Védett faj", null, 0, 0, "A sodrást kedvelő selymes durbincs teste megnyúlt, oldalról összenyomott. Feje és orra hosszú, a szeme nagy. Kis szája csúcsba nyíló vagy félig alsó állású. Hátúszója igen hosszú és elég magas. Alapszíne zöldessárga, hátán és oldalán 3-4 sötétbarna, helyenként megszakadó, keskeny hosszanti csík húzódik, ami alapján minden más halfajtól könnyen elkülöníthető."),
            new ClassHal("Simatok", "Acipenser nudiventris", R.drawable.sima_tok, "Védett faj", null, 0, 0, "A sima tok elterjedési területe a Duna-medencéje (a Duna mohácsi szakaszától lefelé), ritkán előkerül a Tiszából és a Drávából is, emellett az Aral-tóban, a Kaszpi-tengerben és a Fekete-tengerben is élnek állományai. A sima toknak 12-15 hátvértje van, az első hátvért a test legmagasabb pontja. A halnak 55-66 oldalvértje és 12-16 hasvértje is van. Teste feltűnően vaskos, zömök, orra rövid, kúposan tompuló, alsó ajka egységes, középen nem szakad meg. Kerekded keresztmetszetű bajuszszálai a belső oldalukon rojtozottak. A sima tok felül élőhelyétől függően hamuszürke, vörhenyesbarna vagy sötétbarna, oldalai világosabbak. A hasoldal és a vértek piszkosfehérek. Valamennyi úszója enyhén szürke árnyalatú, a hasúszók olykor sötét foltokkal. Testhossza ritkán elérheti a 200 centimétert. Testtömege általában 8-10 kilogramm, legfeljebb 40-50 kilogramm. A sima toknak 4 formája ismert: a Dunában, a Vaskapu-szoros (Románia) felett az édesvízi forma él, amely már nem vándorol a tengerbe, hanem a Vágban, a Tiszában, a Marosban stb. ívik. A három másik forma íváskor a tengerből úszik fel a folyókba. A Kaszpi-tenger körzetében ősszel (a teleléshez kapcsolódva a folyók mély, nyugodt mederrészeibe) vagy tavasszal vándorolnak fel. Valamennyi forma március–májusban ívik. A sima tok tápláléka a vízfenék talajában élő gerinctelen állatok (főként puhatestűek), valamint fenékhalak."),
            new ClassHal("Sujtásos küsz", "Alburnoides bipunctatus", R.drawable.sujtasos_kusz, "Védett faj", null, 0, 0, "A sujtásos küsz patakok, kisebb folyók lakója. Szája kicsi, csúcsba nyíló, de oldalról nézve a szájhasíték enyhén fölfelé irányul. Az oldalvonal fölött és alatt fekete pontokból álló sujtás húzódik, ami főleg a test elején és a szaporodás időszakában feltűnő. Úszói világosszürkék, de mell- és hasúszóinak töve vörhenyes. A közeli rokon szélhajtó küsz szája fölső állású, háta alacsonyabb, mellúszójának töve pedig szürke, a valamennyire hasonlító balin szája nagyobb, egészen a szeme alá ér."),
            new ClassHal("Szilvaorrú keszeg", "Vimba vimba", R.drawable.szilvaorru_keszeg, "Őshonos, fogható faj", "04.15.-05.31", 20, 0, "Eredetileg félsós tengeröblökben élő és onnan szaporáshoz a folyókba fölvándorló faj volt, de idővel állandóan édesvízben maradó populációi is kialakultak – a hazai állományok is ilyenek. Áramláskedvelő faj, így előfordulása nagyobb folyóvizeinkben – a Dunában, a Tiszában, a Drávában – tipikus. A kifejlett egyedek általában 20-30 cm-esek, de a nagyobb példányok a 40 cm feletti méretet is elérhetik. Nyúlánk, oldalról lapított, mérsékelten magas hátú hal. Közepes nagyságú fejéhez mérten hosszú, a szem átmérőjénél nagyobb orra a fölső állkapocs elé nyúlik. Nevét az orra színéről kapta, mely kékes színezetű és kissé gömbölyded. Szája alsó állású, alulról nézve U alakú, ajkai lekerekítettek, nem éles szélűek. Farokalatti úszójában 18-22 elágazó sugár számlálható. Táplálékát rovarlárvák, férgek, egyéb puhatestűek teszik ki, melyeket főleg a vízfenékről gyűjti, mivel többnyire az aljzat közelében tartózkodik. Május-júniusban ívik, az ikrákat a folyók sekély, kavicsos medrében rakja le."),
            new ClassHal("Szivárványos pisztráng", "Oncorhynchus mykiss", R.drawable.szivarvanyos_pisztrang, "Idegenhonos faj", null, 0, 0, "Eredeti élőhelye a Bering-tenger ázsiai és amerikai partvidéke, Magyarországra 1885-ben került, azóta számos vizünkbe betelepítették. Fennmaradása csak az ismétlődő telepítéseknek köszönhető, stabil, önfenntartó állománya nem alakult ki. A sebes pisztránghoz hasonlóan a hegyi és hegylábi kisvízfolyásokat kedveli, de kisebb az oxigénigénye, ezért jobban tűri a víz felmelegedését. Tágabb tűrőképessége folytán olyan helyeken is megél – pl. alföldi holtágakban –, ahol a sebes pisztráng nem maradna meg. Több kilogrammos testtömeget is elérhet, de vizeinkben a 30-35 centi fölöttiek már ritkák. Orsó alakú, oldalról lapított hal, az idősebbek magasabb hátúak. Feje aránylag kicsi, ehhez mérten a szeme nagy. Orra rövid és lekerekített, szája nagy és csúcsba nyíló, benne kisebb fogak sorakoznak. Felső állkapcsának vége a szem alá ér, de nem nyúlik a szem hátsó vonala mögé (ennek alapján jól elkülöníthető az őshonos sebes pisztrángtól). Hátúszójában és farokalatti úszója egyaránt rövid. Farokúszója jól fejlett, a hátsó vége enyhén homorú, és előtte a hátoldalon egy kis zsírúszó található. Pikkelyei aprók, oldalai ezüstösek, középen egy halványlila sávval, ami ívás idején a szivárvány színeiben játszik. A fiatalok oldalán nagyobb szürke foltok sorakoznak, amelyek idővel eltűnnek. Fején, testén és úszóin többnyire igen sok fekete pötty látható. Tápláléka gerinctelen állatokból és kisebb halakból áll. Zsákmányát vízközt, az aljzaton és a víz felszínén is keresi. Szaporodása a patakok fölső szakaszán február-márciusban történik a sebes pisztrángéhoz hasonló módon."),
            new ClassHal("Szivárványos ökle", "Rhodeus amarus", R.drawable.szivarvanyos_okle, "Védett faj", null, 0, 0, "A szivárványos ökle egy magas testű, oldalról erősen lapított, igen apró termetű hal, amelynek mind a háta, mind a hasa erősen ívelt. Orra rövid és tompa, szája kicsi és csúcsba nyíló. A szeme nagy, átmérője körülbelül megegyezik az orr hosszával. Az ivadék és a felnőtt példányok faroknyelén egyaránt egy fémes csillogású kékeszöld sáv látható, ami igen jó megkülönböztető bélyeg."),
            new ClassHal("Széles durbincs", "Gymnocephalus baloni", R.drawable.szeles_durbincs, "Védett faj", null, 0, 0, "A széles durbincs zömök, magas testű, oldalról lapított és néhány szabálytalan harántsávval mintázott barnás sügérféle. A hátúszó hátulsó részének széle domború, és ha a vonalát meghosszabbítjuk, az megközelítőleg derékszögben metszi a faroknyelet. Nagyon hasonlít hozzá a vágódurbincs, de annak oldalát csak szabálytalanul elszórt sötétbarna foltok tarkítják, és a hátúszó szegélyvonalának lefutási iránya más. A sügér zöldes sávozottsága kifejezettebb, és az első hátúszója végén egy fekete foltot visel. Mivel a vágódurbincs sem fogható, elegendő, ha a horgász felismeri, hogy vágó vagy széles durbincsot fogott, hiszen különböző okból, de mindegyiket nyomban vissza kell engednie."),
            new ClassHal("Széles kárász", "Carassius carassius", R.drawable.szeles_karasz, "Őshonos, időszakos felmentéssel fogható faj", null, 0, 0, "A folyószabályozások előtt, amikor az Alföld jelentős része még mocsárvilág volt, leggyakoribb halaink egyike volt, napjainkban állománya – nemcsak Magyarországon, hanem a szomszédos országokban is - megritkult, emiatt a nem fogható, de nem védett halak közé tartozik. A növényekben gazdag sekély állóvizek jellemző hala, folyóvizekből csak igen ritkán, áradások idején kerül elő. Nagyobb állományai az öreg holtágakban és a mocsarakban alakulnak ki. Elviseli az oxigénszegény viszonyokat, télen pedig a víz teljes átfagyását is. Közepes méretű faj, hossza 15-25 cm, ritkán 30 cm fölöttire is megnő. A széles kárász igen magas hátú, oldalról erősen lapított hal, pikkelyei aranysárgák és nagyok. Hátúszója körülbelül a hasúszóval egy vonalban kezdődik, a széle domborúan lekerekített. Feje kicsi, homloka meredeken emelkedik, orra tompa. Csúcsba nyíló kicsi száján nincsenek bajuszszálak. Testét nagy pikkelyek fedik, melyek vastagok és erősen ülnek. Legjobban az ezüstkárász (barnás vizekben még a színe is) és a ponty hasonlít hozzá, de az ezüstkárász hátúszójának széle egyenes vagy kissé homorú, a ponty pedig a fölső állkapcsán és a szájszögletében is kis bajuszt visel. Az egészen fiatal példányok faroknyelén – közvetlenül a farokúszó előtt – általában egy körbefutó fekete gyűrű látható, amelyből később csak egy folt marad, majd az idősebbeknél ez is eltűnik (ilyen mintázatot a fiatal ezüstkárászok nem viselnek). Ívási ideje és helye egybeesik a pontyéval. Mindenevő, táplálékát kisebb gerinctelen állatok, növényi hajtások és magvak, valamint szerves törmelék alkotja. Szaporodása május-júniusra esik. Az ikrákat részletekben rakják le az állóvizek növényekkel sűrűn benőtt részein."),
            new ClassHal("Szélhajtó küsz", "Alburnus alburnus", R.drawable.szelhajto_kusz, "Őshonos, fogható faj", null, 0, 0, "Jellemzően a szabad vízfelületű állóvizekben, és a nem túl nagy sodrású folyóvizekben találkozhatunk vele. A küsz az egyik leggyakoribb apró termetű halunk, fontos szerepet játszik a ragadozóhalak táplálkozásában. A balin mellett kedvelt tápláléka a süllőnek, kősüllőnek és a sügérnek is. Közismert neve a sneci. Megnyúlt, oldalról erősen lapított testű hal. Szája kicsi és felső állású, hasítéka ferdén fölfelé irányul. Orra hegyesedő, szeme nagy. Testhossza általában 10-15, maximum 20 cm, pikkelyei ezüstösek, könnyen leválók. Legjobban a balinivadék hasonlít hozzá, de annak a szája a szeme alá ér. Csapatosan járva a felszín közelében keresi táplálékát, amelyet pl. vízre hulló rovarok, növényi részek és szerves törmelék alkot. Szaporodása elhúzódhat, áprilistól akár júliusig is eltarthat. Ikráit a homokos és sóderes mederfenékre, illetve vízinövényekre rakja."),
            new ClassHal("Sőregtok", "Acipenser stellatus", R.drawable.soregtok, "Védett faj", null, 0, 0, "A sőregtok a Fekete-, Azovi- és Kaszpi-tenger, valamint az odatorkolló folyók lakója. A Dunában Pozsonyig észlelték, napjainkban a Vaskapunál lévő duzzasztómű megakadályozza, hogy felúszhasson a folyóban. Az utolsó ismert magyarországi példányt 1965-ben Mohácsnál fogták. A sőregtoknak 10-15 hátrafelé irányuló, éles hegyű, hátvértje van; a 26-40 finom oldalvért nem fedi egymást; 10-12 a hasvértek száma. Rendkívül karcsú, megnyúlt testének keresztmetszete kör alakú. Keskeny, hegyes, felfelé hajló orrának hossza meghaladja a fej hosszúságának felét. Alsó ajka középen megszakított. Háta rozsdabarna, feketésbarna árnyalattal, a fej oldalai vörhenyesek; hasoldala fehér, ezüstös csillogással. Előfordulhat csaknem fekete színű, szürkéssárga hasoldalú példány is. Orrának alsó fele hússzínű, a csontos vértek piszkosfehérek. A nőstény maximális testhossza 220 centiméter, testtömege 68 kilogramm. A sőregtok vándorhal, van úgynevezett nyári és téli formája. Előbbi többnyire nem hatol messzire a folyók alsó szakaszán: ívóhelyei a folyók torkolatában, gyakran a delta előtt, a görgeteges, hordalékos részeken vannak. Júniusban ívik, az ivadék hamar visszatér a tengerbe. Messzire felúszik a folyókban, június–szeptemberben ívik. Tápláléka rovarlárvák, rákok és apró halak."),
            new ClassHal("Sügér", "Perca", R.drawable.suger, "Őshonos, fogható faj", "03.01.-04.30.", 0, 0, "A kis folyók alsó szakaszának jellemző hala, de jól alkalmazkodik más környezeti feltételekhez is. Megtalálható a nagy folyók lassabb áramlású szakaszain és nagy állományai alakulhatnak ki a növényzetben gazdag, sekély állóvizekben. A mély tavak és holtmedrek a parti sávjában mozog. A nagyobb példányok 20-30 cm hosszúak, kivételesen valamivel nagyobbak is lehetnek. Aránylag magas testű, oldalról lapított hal. Feje és szeme nagy, orra hosszabb, mint szemének átmérője. Szája csúcsba nyíló, de hasítéka kissé fölfelé irányul, benne apró fogak vannak. Fölső állkapcsa hátul a szem középvonaláig ér. Az első hátúszó végén egy nagyon jellemző fekete folt látható. Farokúszója enyhén bemetszett. Hasúszói a mellúszók alatt, de kissé hátrébb foglalnak helyet. Pikkelyei kicsik, de erősen ülnek. Oldalait zöldes-feketés harántsávok díszítik. A kisebb példányok fenéklakó gerinctelenekkel táplálkoznak, amelyeket szinte válogatás nélkül fogyasztanak, az idősebbek emellett egyre több apró halat zsákmányolnak. Áprilisban szaporodik növényekkel benőtt vagy tiszta vizű, köves-kavicsos aljzatú sekély vizekben. Az ikrások által kibocsátott ikraszemek egymáshoz tapadva gyöngyfüzérszerű láncot alkotnak, amely a növényeken vagy a köveken megtapad."),
            new ClassHal("Tarka géb", "Proterorhinus semilunaris", R.drawable.tarka_geb, "Idegenhonos faj", null, 0, 0, "Ez a kis termetű gébfaj az édesvízhez is jól alkalmazkodott és Magyarországon is terjedőben van az állománya. Gazdasági jelentősége nincs, ahol nagyobb számban előfordul ott a ragadozók táplálékaként kap szerepet. Magyarországon nem védett, természetvédelmi értéke nincs. Teste és feje oldalra lapított. Rajta 37-46 nagy, kerek pikkelye van. A felső- és alsó állkapcsok egyforma hosszúak. A hasán tapadókorong található. Úszóhólyaga nincs. Az úszók töve pikkelyezett. Színezete sárgásszürke 4-5 sötét sávval. Úszói csíkosak. A tarka géb legfeljebb 9 centiméter hosszú. A Proterorhinus semilunaris fejének a hossza a testhossz 28-32 százalékát teszi ki.[20] Az első hátúszó érintkezik a második hátúszóval. Szemének átmérője a fej 16-21 százalékát képezi. Közeli rokonaitól a folyami gébtől, a csupasztorkú gébtől, a kessler-gébtől, a feketeszájú gébtől és a szirman gébtől a tarka géb könnyen megkülönböztethető az orrán lévő két rövid csövecskéje miatt. Hasonlít még az amurgébre, a cifra és botos kölöntékre de itt is segít az említett csövecske, valamint ezeknek a fajoknak nincsen tapadókorongszerű hasúszójuk. Eredetileg a brakkvízben élt, de jól alkalmazkodott az édesvízhez is. Tápláléka férgek, rovarlárvák, rákok és olykor halikra vagy hallárva."),
            new ClassHal("Törpe csík", "Sabanejewia aurata", R.drawable.torpecsik, "Védett faj", null, 0, 0, "Előfordul a Kaszpi-tengertől egészen Délkelet-Európáig, azonban jelentős állományai a Duna vízrendszerében találhatóak meg. Elsősorban természeti szempontból értékes kisméretű, áramláskedvelő halfaj. Gazdasági jelentősége nincs, még a ragadozóhalak sem nagyon tudják elejteni, mert beássa magát a fövenybe és így nem találják meg. A Kaszpi-tenger mellett elterülő Turáni-alföld és a Duna vízrendszere az elterjedési területének két legfőbb központja. Előszeretettel tartózkodik mély vizekben, elsősorban a 2 méternél mélyebb részeken lehet velük találkozni, de a Kazán-szorosnál 20 méteres mélységben is megtalálták már. Magyarország területén először 1948-ban igazolták hivatalosan a jelenlétét, de egyes kutatók szerint őshonos halfajunk. A gyors folyású vizeket kedveli, a folyók gyorsabb folyású szakaszain és a középhegységi patakokban él. Legkönnyebben a vágó csíkkal lehet összetéveszteni, mert nagyon hasonlítanak egymásra. A mintázatukban mutatkozó eltérések szolgálhatnak a megkülönböztetés alapjául, hiszen a törpecsík testoldalán nagy és kicsi foltok alkotnak legfeljebb három hosszanti sort, míg a vágócsíkon öt sáv különül el. A további csíkféléktől, már könnyebb megkülönböztetni, hiszen a kövi csík teste hengeres a feje oldalról nem lapított. A réti csík oldalán nincsenek foltok, mert hosszanti sárga és barna csíkok tarkítják. A hal testhossza 8-12 centiméter, legfeljebb 14 centiméter. Felső állkapcsán 6 hosszú bajuszszál ül. Pontosan a szem középvonala alatt kétágú erős tüske van. Egy hosszanti sor mentén 170-200 nagyon kicsi pikkelye van. A hímek a nőstényektől a testoldalon lévő jellegzetes megvastagodás alapján könnyen megkülönböztethetők. Ez az ivari különbség volt az alapja a Sabanejewia nemnek a Cobitis fajoktól történő elkülönítésének a vágó csíknál ez az ivari különbség nem figyelhető meg. A csekély vízhozamú patakokban és az állóvizekben nem fordul elő. Jelentősebb vízhozamú folyóink paduc-, márna- és dévérzónájában él. A dévérzónában a kemény aljzatú sodrottabb szakaszokon, gyakran a folyó sodorvonalában tartózkodik szívesen, de az üledékes szélvizekben már ritkábban fordul elő. A mélyvizek lakójaként a mederfenéken található táplálékok közül az apró fenéklakókat víziférgeket, rovarok lárváit, puhatestűeket és alsóbbrendű rákokat fogyaszt. Oxigénigénye a hazai csíkfajok közül a legnagyobb."),
            new ClassHal("Törpeharcsa", "Ameiurus nebulosus", R.drawable.torpeharcsa, "Idegenhonos, invazív faj", null, 0, 0, "Magyarországra telepített állománya inváziós faj, amennyiben túlságosan elszaporodik, akkor az ikra és az ivadékfogyasztása miatt a természetes vizekben, valamint a tógazdaságokban is kárt tud okozni. Eredeti élőhelye Észak-Amerika keleti része, Magyarországra, 1904-ben került Franciaországból és Németországból. Folyó- és állóvizeinkben egyaránt előfordul, a Balatonba szervezett telepítése is történt. Különösen kedveli a növényzetben gazdag sekély állóvizeket az iszapos fenekű holtágakat és a tavakat. A folyóvizekben is megtalálható, de a patakokban ritka. Nagyobb folyókban a márnazóna alatti rész és a kisebb folyókban a domolykózóna alsó része a sügérzóna az igazi otthona. Teste a folyami harcsáénál zömökebb, a fejtájékán felülről és a farokrészén oldalról lapított testű hal. Háromnyaras korára elérheti a 20–25 centimétert. A Magyarországon előforduló törpék méretüket tekintve kisebbek, mint az őshonos formái. Eredeti élőhelyén elérheti az 55 centiméteres nagyságot és a 2,8 kilogrammos súlyt. Aránylag nagy feje széles, elől lapos az orra hosszú és a szeme kicsi. Alsó és felső állkapcsán 4-4, összesen 8 bajuszszálat visel. A felső állkapcsán lévőkből 2 bajuszszál a szem és az orrnyílása között, a két leghosszabb pedig a szájszögletében helyezkedik el. Széles szájában sűrű kefefogazata van. A hátúszójának elején egy erős, szúrós csonttüske van, melyet 6 úszósugár követ. Farkalatti úszója hosszú, sugarainak száma 17-23, fölötte a hátoldalon egy apró zsírúszó is elhelyezkedik. Bőre pikkelytelen, csupasz és az oldalvonala gyakorlatilag végigfut a testén. Háta sötétbarna, oldala felül sötétbarna, lefelé fokozatosan világosabbra válik, szürke, márványozott, foltokkal. Hasa a szennyes-sárgától a tejfehérig változhat. Legnagyobb példányai ritka esetben akár 25-30 centiméter nagyságúak is lehetnek. A magyar horgászrekord 1,98 kg, melyet 1996-ban regisztráltak. Aktivitása alapján, inkább éjjeli állat. Ritkábban kap nappal, de ahová betelepítették és elszaporodott ott tömegesen bármikor előfordulhat. Fogásához gilisztával felcsalizott fenekező módszer javasolható, de jó eséllyel csontival is fogható és a gyengébb szemű kukoricára is rákap. Fogása nincs korlátozva, sőt az intenzív vizeken indokolt a horgászatuk. Ha egy vízterületen túlságosan elszaporodik, akkor lehetetlenné teheti a hagyományos csalikkal történő horgászatot. Ilyenkor válogatás nélkül mindenre rákap, még a harcsázó szerelésre tűzött nagytestű keszegeket is megtámadja és halálra gyötri. A kifogott példányokkal vigyázni kell, mert úszóin lévű tüskéi szúrósak, ezért célszerű kesztyűvel megfogni őket, hiszen kellemetlen hámsérüléseket tud okozni a horgász kezén."),
            new ClassHal("Vágó csík", "Cobitis taenia", R.drawable.vagocsik, "Védett faj", null, 0, 0, "Európában és Ázsiában honos. Iszapos aljzatú, tiszta álló- és folyóvizek, dombvidéki patakok lakója. A testhossza 8–10 cm, legfeljebb 12 cm-ig centiméter. Feje kicsi, orra hosszú és domború, a hegye tompán lekerekített, teste nyúlánk, oldalról szalagszerűen összenyomott. Apró, fenéklakó gerinctelen szervezetekkel táplálkozik, de szerves törmeléket is fogyaszt. Alkonyatkor indul táplálékot keresni, a nap többi részben a homokba ássa magát vagy kövek alá rejtőzik. Szaporodása szezonális, áprilistól júniusig tart, ragadós ikráit a sekély vízben kövekre vagy növényekre rakja."),
            new ClassHal("Vágódurbincs", "Gymnocephalus cernua", R.drawable.vagodurbincs, "Őshonos, időszakos felmentéssel fogható faj", null, 0, 0, "A durbincsok általános jellemzője, hogy a két hátúszójuk egyesült. A kopoltyúfedő tüskés, a szájukban finom fogazat található, a mell és a has többé-kevésbé pikkelytelen. Álló- és folyóvizekben egyaránt előfordul, de az állóvizek esetén a mélyebb, növényzettől mentes helyeket kedveli, az elmocsarasodó részeket kerüli. Kis növésű halunk, jól fejlett példányai sem haladják meg a 10-15 cm-es hosszúságot. Teste megnyúlt, mérsékelten magas, oldalról összenyomott. Feje és lekerekített orra aránylag hosszú, a szeme nagy. Csúcsba nyíló szája kicsi, fölső állkapcsának vége nem ér a szem alá. Hátúszója hosszú és magas. Az úszó hátulsó részének a széle közel egyenes, és ha a vonalát meghosszabbítjuk, az hegyes szögben metszi a faroknyelet. Farokalatti úszója rövid. Hasúszói előre tolódtak, szinte egy vonalban kezdődnek a mellúszókkal. Pikkelyei közepes nagyságúak. Nagyon hasonlít hozzá széles durbincs, de ennek színe halvány olajbarna, oldalán az apró, sötétbarna pettyekből elmosódó nagyobb foltok, szabálytalan harántsávokká állnak össze. Kezdetben a táplálékát planktonszervezetek, később fenéklakó gerinctelenek alkotják. A nagyobb példányok alkalmilag egy-egy apró ivadékhalat is elfogyasztanak. Ívása április és május folyamán több részletben játszódik le. Az ikra kis füzérek vagy csomók formájában a vízi növényzetre vagy a köves-kavicsos mederfenékre kerül."),
            new ClassHal("Vágótok", "Acipenser gueldenstaedtii", R.drawable.vagotok, "Védett faj", null, 0, 0, "A hazai vizekben vadon élő nagytestű tokfélékkel csakis folyóvizekben találkozhatnánk, azonban sok állóvízbe is telepítettek mesterséges szaporításból származó példányokat, főként vágótokot és különböző hibrideket (melyek szintén automatikusan védelmet élveznek). A pikkelyek helyett vérteket viselő, jellemzően hosszú orrú, erős, húsos, alsó állású szájú és aszimmetrikus farokúszójú halak tokféleként való felismerése nem okozhat gondot. Az egyes fajok elkülönítésére leginkább a száj és a száj előtt induló bajuszszálak alkalmasak. A hazai vizekben leggyakrabban horogra kerülő – nem őshonos, fogható – tokféle a lénai tok. Ennek hosszú és sima bajuszszálai közelebb erednek az orrcsúcshoz, mint a nem túl nagy szájnyíláshoz, s hátrasimítva a felső ajkat elérik, az alsó ajak pedig középen megszakított. A hatósági jóváhagyással foghatóvá tehető kecsege bajuszszálai szintén elérik a felső ajkat, de a kecsege bajuszszálai rojtozottak. Ezektől a többi – védett – tokfaj a száj és a bajuszszálak mérete, alakja alapján is biztosan elkülöníthető: a viza bajuszszálai is elérik a felső ajkat, de hatalmas szája szinte a fej egyik szélétől a másikig ér. A simatok ajkai megszakítás nélküliek, a vágótok és az igen hosszú orral rendelkező sőregtok bajuszszálai nem érik el a felső ajkat."),
            new ClassHal("Vaskos csabak", "Telestes souffia", R.drawable.vaskos_csabak, "Védett faj", null, 0, 0, "A Duna-medence bennszülött faja ezért nagy természeti értéket képvisel. Veszélyeztetett és ezért megóvása élőhelyein, kiemelt fontosságú feladat. Magyarországon csak elvétve átsodródó példányokkal lehet találkozni. A vaskos csabak a Duna és a Rajna középső és felső szakaszának gyors folyású vizeiben él, ritkábban tavakban is előfordul, például megtalálható a Boden-tóban és az Alpokban 850 méterig. Magyarországon a Felső-Tiszán és ennek mellékvizein, a Mura hazai szakaszán lehet elviekben találkozni vele, hiszen nagyon ritka. Egyetlen bizonyított előfordulását 2004-ben a Tiszán Tiszacsécsénél jelentették. Legközelebbi, de határainkon kívül eső populációi a Mura szlovéniai szakaszán, illetve kelet felé a Felső-Tiszában, valamint a Tisza romániai mellékfolyóiban és azok patakjaiban élnek. Testformája és szájállása alapján a nyúldomolykóhoz (Leuciscus leuciscus) és a gyöngyös koncérhoz (Rutilus frisii) hasonlít, de narancsszínű oldalvonala és az a fölött húzódó kékesszürke sáv jó támpontot ad a megkülönböztetésére. A hal teste erősen nyújtott, oldalról lapított. Testéhez viszonyítva aránylag kisméretű a feje és a szemnagysága közepes. Kissé előrenyúló szája félig alsó állású. Orra tompa és az orrnyílás vonalán túlér egy kissé a szájszöglete. 48-56 nagyméretű pikkelye van az oldalvonal mentén. Oldalvonala narancsszínű és fölötte az orrtól a farokúszóig egy kékesszürke sáv húzódik. Kis termetű hal testhossza 12-17 centiméter, ritkán haladja meg a 25 centimétert. 41-42 csigolyája van. A kavicsos szakaszokon fenéken tartózkodó rajhal. Tápláléka a vízfenéken előforduló plankton és apró gerinctelen szervezetek. Elfogyasztja az apróbb kisebb csigákat, bolharákokat, rovarlárvákat, de alkalomadtán algákat és a vízre hulló hernyókat is. Március és május között csapatosan ívik, ilyenkor vízfolyások felső szakaszaira vonul és a sebesen áramló vízben, a kavicsos fenéken rakja le ikráit. Nászidőszakban megfigyelhető, hogy az oldalvonal színe kifejezettebb, emellett az uszonyok tövén, valamint a kopoltyúfedőn is halvány narancsos elszíneződést ölt magára. Az ivarérett hímek zömmel 10-12 centiméter hosszúak, az ikrások jellemzően ennél nagyobbak."),
            new ClassHal("Viza", "Huso huso", R.drawable.viza, "Védett faj", null, 0, 0, "A többi tokfélénél jóval nagyobbra növő és sokáig élő halfaj, hiszen életkilátása a száz évet is meghaladhatja. Európa legnagyobb édesvízi hala endemikus élőlény a Kaszpi- és Fekete-tengeri medencében, és kisebb állománya megtalálható az Adriai-tenger vízrendszerében. Intenzív halászata és az ikrájából készített nagyon értékes beluga- vagy fekete kaviár miatti túlhalászás, és az orvhorgászat eredményeként állománya jelentős mértékben lecsökkent. Horgászata nagyobb mértékben csak a tengerparti országokban lehetséges, de az állománya olyan mértékben megritkult, hogy a washingtoni egyezmény döntése értelmében hivatalosan csak a tenyésztett halak ikráját lehet nemzetközi kereskedelmi forgalomba hozni. Magyarországon is számon tartják az ínyencek a nagyon finomnak tartott és rendkívül drága beluga kaviárt, mely a viza ikrájából is készül. Az általánosnak mondható 113,6 grammos beluga viza-kaviár ára 80 000 forint. Rendkívül lassan, de óriásira növő anadrom faj. A legnagyobb, rekord méretű példány 7,2 méter hosszú és 1571 kilogramm tömegű volt. A vizák a világ legnagyobb édesvízi halai. Teste többé-kevésbé csupasz, rajta 5 sorban elhelyezkedő csontvértek és elszórva csontszemcsék vagy más néven vértpikkelyek (ganoid pikkelyek) fedik. A fején csontos pajzsok találhatók. Négy bajuszszála az alsóállású száj előtt helyezkedik el. Állkapcsa előretolható. Orra tompa, bajuszszálai hengeresek és rojtosak. Szája nagy, alulról eléri a fej két szélét. Alsó ajka középen megszakított, ellentétben a sima tokkal. Legjobban közeli rokonaihoz a valódi tokfélékhez hasonlít, de markáns nagy méretével eltér tőlük. Különböznek továbbá, hogy a vágó toknak nemcsak az alsó ajka megszakított, hanem a felső is két félre osztott. A sima toknak az ajka nincs megszakítva és a bajuszszálai rojtozottak. A sőregtok orra feltűnően hosszú és vékonyabb. A szibériai tok szája kisebb, bajuszszálai közelebb állnak az orrcsúcsához, mint a szájhoz. A kecsege szája kicsi és a bajuszszálai rojtozottak. A viza mozgása kissé nehézkes. Életének egy részét a tengerben, másik részét a folyókban tölti. A fiatal állat a homokban keresgéli férgekből, lárvákból, csigákból és kagylókból álló táplálékát, idősebb korában kisebb halakat is fogyaszt. Egykor minden nagyobb folyónkból halászták; a Drávából, a Tiszából, a Marosból és a Körösökből is. Sajnálatos módon a Kárpát-medencében a többi anadrom tokféle a vágó tok, a sima tok, és a sőregtok is a kihalás szélére került. Az 1970-es években Magyarországon sikeres kísérleteket folytattak a kecsege és a viza keresztezésével. A hibridet “vicsegének” nevezték el. Később azonban a tenyészanyag fenntartás nehézségei miatt, az ilyen programok megszűntek. A magyarországi toktenyésztésnek a VIZA 2020 program ad új lehetőséget. Ez a program a fajok mesterséges szaporítását, telepítését és a még megmaradt élőhelyek rekonstruálását is magában foglalja. Például 2010 szeptemberében visszatelepítési program keretében 100 kishalat engedtek a Dunába. Magyarországon védett faj, természetvédelmi értéke tízezer forint."),
            new ClassHal("Vörösszárnyú keszeg", "Scardinius erythrophthalmus", R.drawable.vorosszarnyu_keszeg, "Őshonos, fogható faj", null, 0, 0, "Gyakori halunk, elsősorban a csendes, nyugodt, hínárral benőtt, holtmedrek, kisebb-nagyobb tavak, keskeny csatornák a kedvelt élőhelyei. Növényi tápláléktól az apróbb rovarokon keresztül a lárvákig mindent elfogyaszt. Oldalról lapított, viszonylag magas testű hal. Hát- és hasvonala erősen ívelt. Háta feketés-zöld, oldala a kifejlett példányoknál aranysárga. Szemgyűrűje aranysárga, esetleg narancsvörös. Úszói élénkpirosak. Pikkelyei erősek, számuk az oldalvonalon 38-42. A bodorkával ellentétben hátúszója a hasúszó alapjánál hátrébb kezdődik, emellett a hasúszók és a farokalatti úszó közötti hasvonal éles. Szája felső állású. Áprilistól júniusig, 15-18 °C-os vízben szaporodik, az ikrákat vízinövényekre rakja."),
    };
}
